css:
@-moz-document domain("kinovod.net"), domain("kinovod230.cc") {
body, .item .desc {
	color: #b5b5bf;
}
.content {
	background: #212c3d;
}
.item .info, .rating-block, .sidebar-telegram, .form-control, .sidebar-nav, .content-wrapper, #sidebar{

	background: #19212f;
}
.sidebar-link, .item .title a {
	color: #d1d1e6;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
	color: #d1d1e6;
	background-color: #19212f;
}
.col-md-9, .col-md-3 {
	width: 100%;
}
}
