css:
@-moz-document url-prefix("https://portal.onovomercado.com.br/") {
.v-main__wrap.v-content__wrap {
    background-color: #021124;
}

header{
    border-bottom: 3px solid #fff;
}

.theme--light.v-card{
    background-color: #041e42;
}

.v-application .secondary--text, .v-application .backgroundDark--text{
    color: #fff !important;
}

a.v-btn{
	background-color: #ed145b !important;
}

a.v-btn.primary--text{
	color: #fff !important;
	border: none;
}

#lessonSearch, .theme--light.v-icon, #newComment{
	color: #fff !important;
}

#lessonSearch::placeholder, #newComment::placeholder{
	color: #fff !important;
}

.theme--light.v-text-field--outlined:not(.v-input--is-focused):not(.v-input--has-state)>.v-input__control>.v-input__slot fieldset{
    color: #fff !important;
}

.theme--light.v-list-item:not(.v-list-item--active):not(.v-list-item--disabled){
	color: #fff !important;
}
}
