css:
/**
 * Wb Aero
 * Status Bar
 * by wifflebunz http://userstyles.org/users/23718
 *
 * Nothin complicated, just changes the status bar a little...
 */

@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul"); 
@-moz-document url("chrome://browser/content/browser.xul") {

#status-bar {
-moz-appearance: none !important;
padding: 0 !important;
margin: 0 !important;
border: 0 !important;
border-top: 1px solid rgba(188,188,184,1) !important;
border-bottom: 0px solid rgba(255,255,255,.2) !important;
height: 21px !important;
max-height: 21px !important;
-moz-border-clip: padding !important;
background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAWCAIAAAB2RJoKAAAAJElEQVQokWP88OEDA27AhEeOgYGB5f///+RL09TwkWr3sPUYALK+L+0SIgrlAAAAAElFTkSuQmCC") !important;
background-position: 0 bottom !important;
background-repeat: repeat-x !important;
background-color: #F0F0F0 !important;
}


#status-bar > statusbarpanel {
-moz-appearance: none !important;
background-color:#aaa !important;
line-height: 16px !important;
height: 21px !important;
max-height: 21px !important;
border: 0 !important;
vertical-align: bottom !important;
border-bottom: 1px solid rgba(245,245,245,.6) !important;
border-top: 1px solid #FFF !important;
border-left: 1px solid !important;
border-right: 2px solid !important;
-moz-border-top-colors: #FFF !important;
-moz-border-left-colors: #FFF !important;
-moz-border-right-colors: rgba(178,178,174,.6) #FFF !important;
background-image: none !important;
background-position: 0 bottom !important;
background-repeat: repeat-x !important;
background-color: transparent !important;
-moz-border-radius: 0px !important;
padding: 0 3px 0 3px  !important;
margin: 0 !important;
}

#status-bar > statusbarpanel.statusbar-resizerpanel {
border-right: 0 !important;
padding: 0 !important;
margin: 0 !important;
}

#status-bar > statusbarpanel > #ilo-statusbar-button {
height: 19px !important;
max-height: 19px !important;
-moz-box-align: center !important;
cursor: pointer !important;
}
#status-bar > statusbarpanel > #ilo-statusbar-button > image {
height: auto !important;
max-height: none !important;
-moz-box-align: start !important;
}


}
