css:
@-moz-document domain(lang-leben.xobor.de) {
.topnavigation_wrap, .navigation_wrap, .login_box, #global-news-box-wrap {
    background: #333;
}

body, td.main {background: #000; color: #fff}

a, table a, table td a {color: #fff}

.header {background-color: #2d3704}

.footer {background-color: transparent}

.main .content {background: #222}

#breadcrumbs, .content a, #breadcrumbs a {color: #fff !important}

.content, .content table {color: #fff}

.content .message a, .mtext a {color: #cef !important}

.fpages, .fpages a, .fpages .crtp_strip, .fpages .crtp {color: #fff; background: #333}

.fpages .crtp, .fpages .crtp.deac {background: #444}

.box .boxheader, .box .boxcontent.message, .footer {background: #333; border-color: #777}

.box .boxcontent, .boxcontent, .foptions_list, .foptions_list_flist {background: #555; border-color: #777}

.umtext {background: #555}

.messageAvatar, .messageinformation, .mtextwrap, .mtextcell {background: #444}

.messageAvatar {box-shadow:none; border-color: #777; border-bottom: none}

.messageinformation {box-shadow:none; border-color: #777; border-top:none; border-top-left-radius: 0; border-top-right-radius: 0}

#messagetext, input, input[type="text"]:focus,  textarea:focus, input[type="password"]:focus {background: #444; color: #fff; box-shadow: none}

td.sidebar, td.reply {background: #333}

.box {box-shadow: none}

.profile, .ratingbutton {background: #444; color: #fff; border-color: #fff}

.profile {border-color: #777}

.umavatar {background: #777}

.snavi {background: #333}

.showmorelist {background-color: #444}

.activeNaviLink {background-color: #555}

.snavi li:hover {background-color: #777}

.leftcell, .rightcell {border-color: #777}

.shead, .profile_background_image {background-image: linear-gradient(to top right, #000 0%, #555 100%)}

textarea, input {border-color: #fff}

textarea, input.button, a.button {background: #444; color: #fff; border-color: #fff; box-shadow: none}

textarea:hover, input.button:hover, a.button:hover {background: #333}

.options {background: #333}

#global-news-li:hover, #global-news-box-wrap span:hover, #news-headline, #news-headline:hover, .topnavigation_wrap li:hover {background: #444; border-color: #777}

ul.navigation, #global-news-box-wrap span {border-color: #777}

ul.navigation li:hover, ul.navigation li.currentpage {background: #444; border-color: #777}

table.vote tr:nth-child(2n+1), .profilerowAboutMe:nth-child(2n), table.line tr:nth-child(2n+1), ul.pmlist li:nth-child(2n+2) {background-color: #555}

ul.scnd li, #activities li, ul.scnd li:hover, #activities li:hover {background: #333}

ul.forum li, ul.scnd li:nth-child(2n+1), #activities li:nth-child(2n+1) {background: #555}

.vote-percent {background: #fff; color: #000}

#analogclock_42 {background: #ccc}

.boardstats tr.actrow, #headerSearchSubmit {background-color: #555}

.attention, .attention a, .attention td {color: #000 !important}

.big_blank_border, #searchres.slim li {background: #333}

#searchres.slim li:hover, #searchres.slim li.act {background: #444}

#forum_search, #searchbox {color: #fff; border-color: #fff}

table.scnd tr:nth-child(2n+1), #activities tr:nth-child(2n+1) {
    background: #444
}

table.forum tr:hover {
    background: #777
}

.foptions_list_flist strong.lnull {
    background: #444;
    border-top: 1px solid #fff;
}

.foptions_list_flist a, .foptions_list_flist strong {
    background: #555;
}

ul.pmlist li.new {
    background: #aa6;
}

small.error, [color="#ff0000"] {
    color: #ff4466;
}

textarea.error, input.error {
    border-color: #ff4466;
}

div.attention, ul.attention {
    background: #d9bb20
}
}
