css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("japan.cnet.com") {
  .frame_col_left, .frame_col_right,
  .col_topstory_left {
    width: 100% !important;
  }

  .frame_col_right .col_300,
  .frame_col_right .col_160, .topstory_box {
    width: 46% !important;
    float: left !important;
    margin-right: 2% !important;
  }

  .block_large_top, .ad_zsponsored_cleaf,
  .block_license, .block_induction_bnet,
  .ad_rect_bottom, .ad_text_top, .ad_rect_top,
  .ad_square_leaf, .ad_text_bottom, .ad_ed_cfeature,
  .block_powercenter_linkbox, .block_ad_adspecial_148,
  .ad_large_footer, .site_logo {
    display: none !important;
  }
  .site_frame {
    width: auto !important;
  }
  .navi_category, .site_tab {
     overflow: hidden !important;
  }
  .site_footer {
    margin-top: 20px !important;
  }
  body {
    min-width: 0% !important;
    background: #fff !important;
  }
  .block_picture {
    background-color: #fff !important;
  }
  .site_logo_block {
    height: 20px !important;
    padding: 15px 15px 0px 15px !important;
  }
  .site_logo_block:after {
    content: 'CENT Japan';
    font-size: 2em;
    font-weight: bold;
  }
  .block_function {
    background: transparent !important;
  }
  .block_function a {
    color: #000 !important;
  }
  .tab_footer ul li a,
  .site_tab ul li a {
    background: #eee !important;
    color: #000 !important;
    border: none !important;
  }
  .tab_footer ul li a.selected, 
  .site_tab ul li a.selected {
    background: #ddd !important;
  }
  .tab_footer {
    background: #eee !important;
    border: none !important;
  }
  .site_tab, .site_tab .tab_search {
    background: #eee !important; 
  }
  .navi_category {
    background: none !important;
    border: none !important;
  }
  .tab_ch_bottom {
    background-color: #fff !important;
  }
  .tab_ch_bottom a {
    color: #555 !important;
  }
}
