@-moz-document domain("teletype.in") {
/* v1.0 */
/* 28.02.21 */
body {
    background: #141414;
    --color-fill-frame: #212121;
}

.input[data-v-5f1472b3],
.input__field[data-v-5f1472b3] {
    background: #212121;
}

:root,
:root[data-theme="auto"],
:root[data-theme="light"],
[data-theme="auto"],
[data-theme="light"] {
    --color-input-border-inactive: #292929;
    --color-input-border-active: #292929;
    --color-input-background: #212121;
    --color-fill-hover-2: #515151
}

.blogArticleCut[data-v-f86b5446]:hover {
    border-color: #828282;
    border: 1px solid #828282;
}

.blogArticleCut[data-v-f86b5446] {
    border: 1px solid #515151;
}

.blog__section[data-v-33b42a66] {
    border-top: 1px solid #212121;
}

.text .code,
.text code,
.text pre {
    background-color: #212121;
}

.article__info[data-v-4d1f73de] {
    border-top: 1px solid #141414;
}

.articleReactions__reaction[data-v-68e829ed],
.articleReactions__add[data-v-68e829ed] {
    border: 1px solid #515151;
}

.settings__aside {
    border-right: 1px solid #222222;
    background: #212121;
}

.checkbox__field_input:checked + .checkbox__field_box[data-v-654f0676] {
    background-color: #4c76e1;
    border: 1px solid #4c76e1;
}

.tipNote[data-v-a61cf42c] {
    background-color: #292929;
}

.settings__field_title.m_list {
    border-bottom-color: #212121;
}

.article__container[data-v-4d1f73de] {
    border-bottom: 1px solid #141414;
}

.menu__buttons[data-v-040acc40] {
    border-right: 1px solid #515151;
}

.editorInlineToolbar[data-v-7aee16ca] {
    border-color: #212121;
}

.button.m_color_muted[data-v-45d99377] {
    border-color: #141414;
}

.blog__section[data-v-33b42a66],
.editorBlockToolbar__section[data-v-94f93e68],
.editorBlockToolbar__search[data-v-94f93e68] {
    border-bottom: 1px solid #515151;
}

.topics.m_empty[data-v-e272fd3a] {
    background: #313131;
}

.editorPublisher__menu[data-v-1be8b3d1] {
    background: #212121;
    border-left: 1px solid #212121;
}

.radio__field_box[data-v-1b319969] {
    border: 1px solid #4c76e1;
}

.newBlog__caption[data-v-d53d34a0],
.notifications__sections[data-v-fb9b2fc6],
.settings__aside_group,
.dropdown__caption {
    border-bottom: 1px solid #212121;
}

html {
    background-color: #141414;
}

* {
    color: #828282 !important;
}

a[href],
a[href] > * {
    color: #60A0BF !important;
}
}
