css:
/* i really want this to be global */
.modal-view .settings-modal-panel .theme-tab {
  display: grid;
  position: relative;
}
.modal-view .settings-modal-panel .theme-tab .preview-container {
  order: -1;
  position: sticky;
  top: 0;
  z-index: 999999;
  margin-top: 0;
}
.modal-view .settings-modal-panel .theme-tab .color-container {
  grid-gap: 20px;
}
