css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#fbPanelBar1-tabBox {
    height: 24px !important;
    font-family: "Segoe UI" !important;
    background: url(data:image/png;base64,) center !important;
}

#fbSearchBox {
    margin-top: -2px !important;
}

.panelTab-text {
    padding-bottom: 7px !important;
    font-weight: normal !important;
    color: #FFF !important;
    text-shadow: 0 0 0.5em #000;
}

#fbToolbar {
    height: 22px !important;
    font-family: "Segoe UI" !important;
    margin-top: -7px !important;
}

#fbPanelBar2-tabBox {
    height: 19px !important;
    font-family: "Segoe UI" !important;
    margin-top: -4px !important;
}

#fbPanelBar2-panelTabs .panelTab-text {
    color: #000 !important;
    text-shadow: none;
}

#fbBreakOnNextButton {
    padding-bottom: 5px !important;
}

#fbMinimizeButton {
    display: none !important;
}

#fbDetachButton {
    margin-right: -5px !important;

}

.toolbar-text-button, #fbContentBox toolbarbutton {
    -moz-appearance: none !important;
    border-color: transparent !important;
}

#fbContentBox toolbarbutton:hover {
    opacity: .4 !important;
}

#fbCloseButton {
    margin-right: -15px !important;
}

#fbCloseButton > .toolbarbutton-text {
    display: none !important;
}

#fbDebuggerButtons {
    padding: 6px 0px 4px 26px !important;
}
