css:
@-moz-document domain("music.youtube.com") {
ytmusic-player:not([player-ui-state_="FULLSCREEN"]) #player {
    margin: auto 42px!important;
}
ytmusic-player[player-ui-state_="FULLSCREEN"] #player {
    margin: 0!important;
}
ytmusic-player-page:not([video-mode_]):not([player-fullscreened_]) #player.ytmusic-player-page {
    max-width: 1200px;
    border-right: 2px dotted #797979b3;
    border-bottom: 2px dotted #797979b3;
    border-top: 2px dotted #797979a8;
}
#thumbnail {
    box-shadow: 4px 4px 16px #f5f5f514, 4px 4px 16px #4848480a;
}
* {
    transition: all 0.13s;
    font-family: 'SF Display'!important;
}
.slider-knob-inner.paper-slider,
.slider-knob.paper-slider {
    transition: all 1.60s;
}
.style-scope .paper-progress {
    transition: all 1.60s;
}
#song-image {
    position: sticky;
    border-radius: 1px;
    background: #0000;
    box-shadow: 0px 2px 10px 1px #0009, inset 0px 0px 20px 2px #0006, inset 0px 0px 20px 2px #0006, inset 0px 0px 5px 1px #fff9, inset 0px 0px 0px 1px #fff3;
    overflow: clip;
}
ytmusic-player-page:not([video-mode_]):not([player-fullscreened_]) yt-img-shadow[id="thumbnail"][object-fit="COVER"] {
    margin: 5px 3px 5px 8.45%;
    display: inline-block;
    border-radius: 3px;
    background: transparent none repeat scroll 0% 0%;
    box-shadow: #0009 0px 2px 10px 1px, #0006 0px 0px 20px 2px inset, #fff9 0px 0px 5px 1px inset, #fff3 0px 0px 0px 1px inset;
}
ytmusic-player-page:not([video-mode_]):not([player-fullscreened_]) yt-img-shadow[object-fit="COVER"] img.yt-img-shadow {
    width: 100%;
    height: 100%;
    object-fit: inherit;
    box-shadow: -3px 0 6px #5e5e5e, -4px 0 5px #000, -6px 0 4px #5e5e5e, -8px 0 3px #000, -10px 0 3px #5e5e5e, -12px 0 3px #000, -14px 0 3px #5e5e5e, -16px 0 3px #000, -18px 0 3px #5e5e5e, -20px 0 3px #000, -22px 0 3px #5e5e5e, -25px 0 3px #0b0b0b, -27px 0 3px #625f5f, -29px 0 3px #000, -31px 0 3px #5e5e5e, -33px 0 3px #000, -35px 0 3px #5e5e5e, -37px 0 3px #000, -39px 0 3px #5e5e5e, -41px 0 3px #000, -43px 0 3px #5e5e5e, -45px 0 3px #000, -47px 0 3px #5e5e5e, -49px 0 3px #000, -51px 0 3px #5e5e5e, -53px 0 3px #000, -55px 0 3px #5e5e5e, -57px 0 3px #000, -59px 0 3px #5e5e5e, -61px 0 3px #000, -63px 0 3px #5e5e5e, -65px 0 3px #000, -67px 0 3px #5e5e5e, -69px 0 3px #000, -71px 0 3px #5e5e5e, -73px 0 3px #000, -75px 0 3px #5e5e5e, -77px 0 3px #000, -79px 0 3px #5e5e5e, -80px 0 7px #000, -82px 0 7px #5e5e5e;
}
ytmusic-player-page:not([video-mode_]):not([player-fullscreened_]) #song-image.ytmusic-player {
    padding-top: 92.211%;
}
ytmusic-player {
    background-color: #131313;
}
video.video-stream {
    width: 100%;
    height: auto;
}
ytmusic-player:not([player-ui-state_="FULLSCREEN"]) #song-video {
    box-shadow: 0 0px 32px #ffffff0d;
}
}
