css:
@-moz-document domain("gamefaqs.com") {

/*========== STATIC COLORS ==========*/

/* Links */
a,
#content .content_qna fieldset.searchbox input.submit_search{
color:#8AF !important; /*03C */
}

/* Visited Links */
#main_col .today .head .date,
a:visited {
color:#58C !important; /*58C */
}


/* Body background and text color*/
#masthead,#content,html,input[type="password"],input[type="text"],textarea,select,.pod,ul.content_nav li.hi,body,#platformlist .nav b,#content .head h1 {
background:#FFF!important; /*FFF */
color:#333!important; /*333 */
}

/* Primary Background Color AND Text color for primary colored elements*/
#header, #header .mh_wrap,#content thead th, #content tr.head th,#content .pod div.head,#content .gs div.head h1, #content .gs div.head .title,#loginbox,#content .pod div.head h1,#content .content_qna .view_block,#content .content_qna .header_small,.details div[style^="background: rgb(0, 0, 255)"],#content .content_qna .qna_table th,.poll div.bar{
background: #333 !important; /*6564FF */
color: #FFF !important; /*#FFF */
}




/*========== DYNAMIC COLORS (transparent and rgba stuff) ==========*/

.gs{
display:none !important;
}

table.topics,
.ffaq h3{
color:inherit !important;
}

#header #loginbox .nav a {
color: hsla(0,0%,100%,.8) !important;
border-color: hsla(0,0%,100%,.5) !important;
text-shadow: 0px 0px 1px rgba(255,255,255,.1) !important;
}

#content .pod div.head {
border-color: rgba(0,0,0,.2) !important; /*4A49C0*/
}

table.board tr:not(.gamefox-highlight) td:not(.gamefox-highlight),
table.board th,
.board_nav .body,
.searchtopics,
#content .content_qna .module,
#content .content_qna .qna_table tr.footer,
#content .content_qna .qna_table tr.highlighted,
#content .contrib .body,
#content ul.systems li.even,
#main_col .topten ol li.alt,
tbody.even td, tbody.even th,
tr.even:not(.gamefox-highlight) td:not(.gamefox-highlight), tr.even th,
#content .qna_hub #side_col li.alt,
#content .content_qna .accepted_answer .title_bar,
#content .content_qna .title_bar {
background: hsla(0,0%,65%,.2) !important; /*#EEE*/
}

#quicknav a, #quicknav p, #masthead .mh_wrap,
#platformlist,
.gutter,
#container .content_filter .wrap,
#content .content_qna div.qna_table,
#content #side_col,
#mpu_col_wrap, #sky_col_wrap,
#side_col .pod .body, #side_col .pod .foot,
.data,
.data span,
table.board tr.even:not(.gamefox-highlight) td:not(.gamefox-highlight), table.board tr.even th,
#content .content_qna .header,
#content .qna_hub #side_col .header,
.ffaq div.ftoc ,
.releases td,
#content .pod .message{
background: none !important; /*#FFF*/
}


html{
overflow-x:hidden !important;
}

#masthead{
height:auto!important;
margin:0 auto 0px auto !important;
}


#masthead > *:not(#quicknav):not(#platformlist){
border-bottom:1px solid hsla(0,0%,20%,.8) !important;
}

#loginbox{
margin-top:8px !important;
padding-top:0 !important;
background:hsla(0,0%,0%,.3) !important;
-moz-border-radius:10px !important;
}

#header #loginbox .nav a {
color: hsla(0,0%,100%,.8) !important;
border-color: hsla(0,0%,100%,.5) !important;
text-shadow: 0px 0px 1px rgba(255,255,255,.1) !important;
}


#searchbox{
background:hsla(0,0%,50%,.5) !important;
}


input.go, span.go, .btn, .btn .btn_wrap {
background-image:none !important;
}


#searchbox form,
.btn,
.btn_wrap,
.btn button, .btn input,
#searchbox .mh_wrap{
overflow:visible !important;
}

#searchbox .mh_wrap{
height:auto !important;
}


#searchbox .mh_wrap:after{ content: "\0020" !important; 
display: block !important;
height: 0 !important;
overflow: hidden !important;
clear:both !important;
}


#quicknav{
border-top: 1px solid hsla(0,0%,80%,.2) !important;
background: hsla(0,0%,50%,.2) !important;
}

#quicknav a{
border: 1px solid hsla(0,0%,50%,.2) !important;
border-width:0 1px !important;
-moz-border-radius:2px !important;
}

#platformlist{
color:inherit !important;
-moz-box-shadow:inset 0px 15px 15px -15px currentcolor;
border: 1px solid currentcolor !important;
border-width:1px 0 !important;
}

#platformlist .nav,
#loglink{
font-size:0 !important;
}

#platformlist .nav *,
#loglink *{
font-size:11px !important;
position:relative!important;
}

#platformlist .nav a,
#loglink a{
margin:0 !important;
padding:0 5px !important;
border:1px solid hsla(0,0%,20%,.8) !important;
border-width:0 0 0 2px !important;
}


#platformlist .nav a:first-of-type,
#loglink a:first-of-type{
border:none !important;
}

#platformlist .nav b{
background:none !important;
}


body > #header,
body > #footer{
border-color:hsla(0,0%,50%,.3) !important;
}


#container{
padding: 0 0 20px !important;
-moz-box-shadow:inset 0 20px 15px -20px currentcolor;
float:none !important;
background:hsla(0,0%,50%,.3) !important;

}

#container:after{
content:'' !important;
display:block !important;
clear:both !important;
}

#container .content_filter .wrap{
padding:0 !important;
}



#content{
border: solid 1px currentcolor !important;
-moz-box-shadow: 0 0 20px currentcolor;
}



input[type="text"],textarea,select,
input[type="password"]{
-moz-appearance:none !important;
border:1px inset currentcolor !important;

}

#side_col .pod .body, #side_col .pod .foot {
border: 1px solid hsla(0,0%,50%,.5) !important;
border-width: 0px 1px 1px !important;
-moz-border-radius:0 0 8px 8px !important;
}

#content .head{
border-color: hsla(0,0%,50%,.5) !important;
}

#content .content_filter .body{
padding:1em .75em !important;
background:hsla(0,0%,60%,.2) !important;
-moz-border-radius:.5em !important;
border: 1px solid hsla(0,0%,50%,.2) !important;
}

#content .pod div.head {
border-color: rgba(0,0,0,.2) !important; /*4A49C0*/
}

.content_filter .genre {
border-color:hsla(0,0%,50%,.2) !important;
}

div.content_nav{
border-bottom: 1px solid currentcolor !important;
background:none !important;
overflow:visible !important;
}

ul.content_nav{
margin-bottom:-1px !important;
}
ul.content_nav li,
ul.content_nav li a,
ul.content_nav li a b {
background:none !important;
}
ul.content_nav li{
-moz-box-shadow:inset 0 -20px 20px -20px currentcolor;
border: 1px solid currentcolor !important;
margin-right:-1px !important;
}


ul.content_nav li:first-child {-moz-border-radius:10px 0 0 0 !important;}
ul.content_nav li:last-child {-moz-border-radius:0 10px 0 0 !important;margin-right:0 !important;}
ul.content_nav li.hi,
ul.content_nav li.hi a {-moz-box-shadow:none !important;color:inherit !important;}
ul.content_nav li.hi{padding-bottom:1px !important;border-bottom-width:0 !important;}

table.topics{
color:inherit !important;
}

table.message td,table.message {border-color:hsla(0,0%,50%,.8) !important;}



/* ANSWERS ARE DUMB */
#content .content_qna .header h2,
#content .content_qna .accepted_answer .title_bar h3, #content .content_qna .accepted_answer .title_bar span,
#content .content_qna .title_bar span {
color:inherit !important;
}

#content .content_qna .question_pod,
#content .content_qna .accepted_answer,
#content .content_qna .answer_pod {
background:none !important;
}

#content .content_qna .view_block {
border-color: rgba(0,0,0,.2) !important;
}




#content .pod div.head {
border-color: rgba(0,0,0,.2) !important; /*4A49C0*/
}

.gs{
display:none !important;
}

.data,
.data span {
padding:0 !important;
border:0 !important;
overflow:visible !important;
}

form ol.fields {
overflow:visible !important;
display:block !important;
}

#loginbox li.submit{
height:60px!important;
}
#loginbox span.submit,
#loginbox li.submit span.submit span,
#loginbox li.submit{
border:none !important;
height:auto !important;
width:auto !important;
background:none !important;
overflow:visible !important;
}
/*#loginbox span.submit * {background:none !important;}*/

input[type="submit"],
button[type="submit"]{text-transform:capitalize !important;}




/*========== IMAGE STUFF ==========*/

#logo{background-image:url(http://dl.dropbox.com/u/386823/GameFAQs/recolor/cleanlogo.png) !important;}

img[src$="/images/default/rec.gif"], img[src$="/images/default/s3.gif"], img[src$="/images/default/s2.gif"], img[src$="/images/default/s1.gif"], #board_wrap table.board.topics img[src$="/images/default/topic.gif"], #board_wrap table.board.topics img[src$="/images/default/topic_archived.gif"], #board_wrap table.board.topics img[src$="/images/default/sticky.gif"], #board_wrap table.board.topics img[src$="/images/default/topic_closed.gif"], #board_wrap table.board.topics img[src$="/images/default/sticky_closed.gif"] { margin: 0pt 1px ! important; padding: 16px 16px 0pt 0pt ! important; background: transparent url(data:image/png;base64,) no-repeat scroll center center ! important; display: inline-block ! important; height: 0px ! important; width: 0px ! important; vertical-align: top ! important; -moz-background-clip: border ! important; -moz-background-origin: padding ! important; -moz-background-inline-policy: continuous ! important; }
#board_wrap table.board.topics .gamefox-status { vertical-align: top ! important; height: 2em ! important; }
img[src$="/images/default/rec.gif"] { background-image: url(data:image/png;base64,) ! important; }
img[src$="/images/default/s3.gif"] { background-image: url(data:image/png;base64,) ! important; }
img[src$="/images/default/s2.gif"] { background-image: url(data:image/png;base64,) ! important; }
#board_wrap table.board.topics img[src$="/images/default/topic_archived.gif"] { background-image: url(data:image/png;base64,) ! important; }
#board_wrap table.board.topics img[src$="/images/default/sticky.gif"], #board_wrap table.board.topics .sticky-start { background-image: url(data:image/png;base64,) ! important; }
#board_wrap table.board.topics img[src$="/images/default/topic_closed.gif"], #board_wrap table.board.topics .topic_closed-start { background-image: url(data:image/png;base64,) ! important; }
board_wrap table.board.topics img[src$="/images/default/sticky_closed.gif"], #board_wrap table.board.topics .sticky_closed-start { background-image: url(data:image/png;base64,) ! important; }


#content .pod div.head {
border:0 !important;
background-image: -moz-linear-gradient(270deg,
  rgba(000,000,000,.1)   0%,
  rgba(255,255,255,.1)  50%,
  rgba(000,000,000,.3)  50%,
  transparent 100%
) !important;
-moz-box-shadow:
  inset 0 0 1px 1px rgba(000,000,000,.1);
}




/*========= GAMEFAQS======*/
#gamefox-quickpost-form{
width:100% !important;
display:inline-block !important;
text-align:center !important;
}

#searchbox ol.fields,
#searchbox ol.fields > li,
#searchbox ol.fields > li > *{
float:none !important;
display:inline-block !important;
vertical-align:middle !important;
overflow:visible !important;
height:auto !important;
padding:0 !important;
border: none !important;
}


.btn, .btn button, .btn input, .btn span, .btn button span {
color:inherit !important;
height:inherit !important;
width:inherit !important;
position:inherit!important;
line-height:inherit !important;
float:none !important;
}

button,
input[type="submit"],
input[type="button"],
input[type="reset"]{
color:inherit !important;
background: rgba(255,255,255,.3) !important;
-moz-appearance: none !important;
border:1px solid currentColor !important;
-moz-border-radius:3px !important;
-moz-box-shadow: 0px 0px 5px hsla(0,0%,50%,1) !important;
/*-moz-box-shadow: 0px 0px 5px hsla(0,0%,100%,1) !important;*/
width:auto !important;
} 


#searchbox .mh_wrap {
height:inherit !important;
padding:0 !important;
}

#searchbox form {
height:inherit !important;
}


#searchbox {
color:inherit !important;
font:inherit !important;
padding:3px 0 !important;
}

.searchtopics form {
width:auto !important;
height:auto !important;
padding:0 !important;
}
.searchtopics label {
float:none !important;
display:inline-block !important;
margin:0 !important;
}

.searchtopics .data{
float:none !important;
display:inline-block !important;
width:330px!important;
}

.u_search .searchbtn,
.searchtopics .btn {
float:none !important;
display:inline-block !important;
margin:0 !important;
vertical-align:middle!important;
}
#content .pagejumper {
text-align:center !important;
margin:0 auto !important;
}

#content .pagejumper li {
display:inline-block !important;
float:none !important;
}
}
