css:
@-moz-document url-prefix("https://laravel.com") {

:root {
  --fg: #f0f0f0;
  --bg: #222;
  --border: #525252;
  --well: #303030;
}
	
body {
  color: var(--fg) !important;
  background-color: var(--bg) !important;
}

h1, th, td {
    color: var(--fg) !important;
}
    
.bg-white {
    background-color: var(--bg) !important;
    --bg-opacity:0 !important;
    color: var(--fg) !important;
}
    
[x-data="searchComponent()"] .text-gray-900 {
    color: var(--fg);
    stroke: var(--fg);
}
 
[x-data="searchComponent()"] .bg-gray-100 {
    background-color: var(--well) !important;
}
    
[x-data="searchComponent()"] .search-result.bg-gray-100 {
    background-color: var(--border) !important;
}
    
.placeholder-gray-900::placeholder {
    color: var(--fg) !important;
}

	nav.main,
nav.main a.brand,
nav.main ul.main-nav,
nav.main .switcher,
.sidebar {
  border-color: var(--border);
}
    
.from-gray-100 {
    --gradient-from-color: unset !important;
}
    
    footer {
        background: var(--bg) !important;
    }
    
    footer .bg-gray-100 {
        background-color: var(--bg) !important;
        color: var(--fg) !important;
    }
    
    footer .bg-white {
        border-radius: 4px;
        background-color: var(--well) !important;
    }
    
    p, a, li, h1, h2, .text-gray-700 {
        color: var(--fg) !important;
    }
    
    p > a {
        color: #ff2d20 !important;
    }
    
    code {
        background-color: var(--well) !important;
        padding: 0 0.35rem !important;
    }
    
    .callout {
        background-color: var(--well) !important;
    }
    
    .callout code {
        background-color: var(--bg) !important;
    }
    
    code, pre, .callout, .bg-purple-600, .bg-red-600 {
        border-radius: 4px;
    }
    
nav.main ul.main-nav > li > a {
  color: var(--fg);
}

.docs article h2 a,
.docs article h2 a:hover {
  color: var(--fg);
}
.docs article h1 + ul {
  border-color: var(--border);
}

.docs article table td,
.docs article table th {
  border-color: var(--border);
}

.docs blockquote {
  opacity: 0.95;
}
.docs blockquote.has-icon .tip {
  background-color: #3683C0;
}
.docs blockquote.has-icon .note {
  background-color: #ac4542;
}

:not(pre) > code[class*="language-"],
pre[class*="language-"] {
  background-color: var(--well);
}
code[class*="language-"],
pre[class*="language-"] {
  color: var(--fg);
  text-shadow: none;
}
.token.builtin, .token.char, .token.inserted, .token.selector, .token.string {
  color: #80CE84;
}
.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url,
.token.function {
  color: #bbb;
}
.token.cdata, .token.comment, .token.doctype, .token.prolog, .token.punctuation {
  color: #aaa;
}
.token.atrule, .token.attr-value, .token.keyword {
  color: #65bee4
}
.token.attr-name, .token.boolean, .token.constant, .token.deleted, .token.number, .token.property, .token.scope, .token.symbol, .token.tag {
  color: #EC7C71;
}

footer.main {
  background-color: var(--well);
}
footer.main > ul > li > a {
  color: var(--fg);
}

}
