css:
@-moz-document url-prefix("https://www.funcraft.net/"), url-prefix("https://www.funcraft.fr/") {
body {
    background: #131313 !important;
    color: #555454 !important;
}
.box {
    background-color: #171717 !important;
}
.player-banner .head {
    float: left;
    background: #fff5f500 !important;
    padding: 4px;
    box-shadow: 0 0 5px rgb(0 0 0 / 50%);
    border-radius: 5px;
}
.player-banner .head img {
    width: 170px;
    height: 170px;
    background: #fff5f500 !important;
    border-radius: 4px;
}
.players-heads .head img {
    width: 48px;
    height: 48px;
    background: #fff5f500 !important;
    border-radius: 5px;
}
.div.lbl.lbl-me {
    display: inline-block;
    padding: 2px 11px;
    border-radius: 4px;
    background: #202020;
    color: #fff;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    background-color: #171717;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #555;
    cursor: default;
    background-color: #171717;
    border: 1px solid #fff5f500 !important;
    border-bottom-color: #171717;
}
.nav-tabs {
    border-bottom: 1px solid #414141;
}
.nav-tabs>li>a:hover {
    border-color: #fff5f500 #fff5f500 #fff5f500 !important;
}
.nav>li>a:focus, .nav>li>a:hover {
    text-decoration: none;
    background-color: #171717;
}
rect.highcharts-background {
    x: 0;
    y: 0;
    width: 0;
    height: 0;
    fill: rgb(255, 255, 255);
}
.player-stats-game .player-stats-info .stats-entry {
    background: #171717 !important;
}
.form-control {
    background-color: #141414;
    background-image: none;
}
.btn-default, .btn-default.disabled, .btn-default.disabled:active, .btn-default.disabled:focus, .btn-default.disabled:hover {
    color: #5e5e5e !important;
    background-color: #151515 !important;
    border-color: #080808 !important;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    text-align: left;
    list-style: none;
    background-color: #171717;
    background-clip: padding-box;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 4px;
    box-shadow: 0 6px 12px rgb(0 0 0 / 42%);
}
.dropdown-menu .divider {
    height: 1px;
    margin: 9px 0;
    overflow: hidden;
    background-color: #696969;
}
.alert-info-small {
    color: #7b7b7b;
    background-color: #141414;
    border-color: #3f3f3f;
}
.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    padding: 8px;
    line-height: 1.428571429;
    vertical-align: top;
    border-top: 1px solid #3c3c3c;
}
.nav-games-survival, .nav-games-blitz, .nav-games-hikabrain, .nav-games-infected, .nav-games-landrush, .nav-games-mma, .nav-games-pvpsmash, .nav-games-rush, .nav-games-rushretro, .nav-games-shootcraft, .nav-games-freecube, .nav-games-skywars, .nav-games-lobby {
    background: #fff5f500 !important;
}
.leaderboard-table thead>tr {
    background: #191919;
}
.table-hover>tbody>tr:hover, .table>tbody>tr.active>td:hover, .table>tbody>tr.active>th:hover, .table>tbody>tr>td.active:hover, .table>tbody>tr>th.active:hover, .table>tfoot>tr.active>td:hover, .table>tfoot>tr.active>th:hover, .table>tfoot>tr>td.active:hover, .table>tfoot>tr>th.active:hover, .table>thead>tr.active>td:hover, .table>thead>tr.active>th:hover, .table>thead>tr>td.active:hover, .table>thead>tr>th.active:hover {
    background-color: #121212;
}
.podium-1-bg {
    background: #FFF8A3 !important;
}
.podium-2-bg {
    background: #F3F3F3 !important;
}
.podium-3-bg {
    background: #FFD597 !important;
}



.game-ulbg-rush, .game-ulbg-hikabrain, .game-ulbg-skywars, .game-ulbg-rushretro, .game-ulbg-survival, .game-ulbg-shootcraft, .game-ulbg-landrush, .game-ulbg-pvpsmash, .game-ulbg-infected, .game-ulbg-blitz, .game-ulbg-mma {
    background-color: #ffffff00 !important;
}
}
