css:
@-moz-document url-prefix("https://zergpool.com/") {
  
  /* By Thamer Alfuraiji ////
//// Social @Alfuraiji ///// */
  
  
body {
    background-color: #212121;
    color: #717171;
}
canvas.jqplot-grid-canvas {
    mix-blend-mode: color;
}
.jqplot-xaxis {
    bottom: -3px !important;
}
.jqplot-title {
    margin-top: -15px;
}
.main-left-inner {
    padding: 1% 2% 2% 2%;
    background-color: #191919;
    border-radius: 0 0 10px 10px;
    border: 2px solid #004035;
}
.main-left-title {
    background-color: #004035;
    border-radius: 10px 10px 0 0;
    padding: 11px 0 11px 21px;
}
.main-left-box {
    border: none;
}
table.dataGrid3 td {
    border-bottom: 1px solid #2b2b2b;
}
table.dataGrid3 tbody {
    border: 1px solid #2b2b2b;
}
table.dataGrid3 th {
    border-bottom: none;
    background-color: #2b2b2b;
}
table.dataGrid3 tbody td {
    width: none;
}
table.dataGrid3 {
    color: #717171;
    margin-bottom: 2px;
}
.ssrow {
    border-top: 0 !important;
}
.ssrow:hover {
    background-color: #001310;
}
.ssrow2:hover {
    background-color: #001310;
}
#resume_update_button {
    color: rgb(255 0 0) !important;
    background-color: rgb(25 25 25) !important;
    border: 0 !important;
    border-radius: 10px !important;
    padding: 10px !important;
    margin-left: 6px !important;
    margin-right: 6px !important;
    margin-top: 3px !important;
    cursor: pointer !important;
    box-shadow: 0px 0px 54px #ff0000 !important;
}
.ui-widget-content {
    border: 0;
    background: #464444;
    color: #a7a7a7;
    margin-top: 7px;
    padding-top: 2px;
    padding-bottom: 2px;
}
.ui-widget-header {
    border: 1px solid #464444;
    background: #007d2c;
    color: #000;
    font-weight: 700;
    padding-top: 2px;
    padding-bottom: 2px;
    margin-top: -3px !important;
}
button {
    background-color: #002b24;
    color: #8c8c8c;
    border: none;
    padding: 5px 20px 5px 20px;
    border-radius: 5px;
}
button:hover {
    background-color: #00987e;
    color: #fff;
}
table.dataGrid3 th.tablesorter-headerAsc, table.dataGrid3 th.tablesorter-headerDesc {
    color:#717171 !important;
}
a, a:visited {
    color:#717171 !important;
}
.page .tabmenu-out {
    background-image : none;
    background-color: #060606;
}
.page .tabmenu-inner .selected {
    background-image : none;
    border:none;
    background-color: #191919;
}
img {
    opacity: 0.8;
}
span.block.immature {
    background-color: #9a7034;
}
span.block.exchange {
    background-color: #5f845f;
}
span.block.cleared {
    background-color: #867474;
}
span.block.confirmed {
    background-color: #608060;
}
.main-text-input {
    background-color: rgb(43 43 43);
    border-style: none;
    margin-bottom: 8px;
    width: 88%;
    color: #8e8e8e;
}
.main-text-input:focus {
    outline: none;
    color: #8e8e8e;
}
select.pagesize {
    background-color: #2b2b2b;
    color:#717171;
}
select.gotoPage {
    background-color: #2b2b2b;
    color:#717171;
}
input.ts-startRow {
    background-color: #2b2b2b;
    color: #717171;
    border: 1px solid;
}
option {
    color: #ffffff;
}
.main-submit-button {
    background-color: #002b24;
    color: #8c8c8c;
    border: none;
}
.main-submit-button:hover {
    background-color: #00987e;
    color: #fff;
}
}
