css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul") {

/* CLOSE BUTTON ON SINGLE TAB */

.tab-close-button[tabindex="-1"] {
 display: -moz-box !important;}

/* MARK UNREAD TABS */

#content tab:not([selected]) {
 color: #0000FF !important;}

/* NEW TAB BUTTON */

.tabs-container .tabs-newtab-button {
 background: #E0F0FF url("http://img263.imageshack.us/img263/1918/tab2.png") !important;
 border-style: solid !important;
 border-width: 2px 2px 0 2px !important;
 margin-top: 4px !important;
 margin-right: 4px !important;
 padding-top: 2px !important;
 width: 32px !important;
 -moz-border-top-colors: #A0A0A0 #FFFFFF !important;
 -moz-border-right-colors: #A0A0A0 #FFFFFF !important;
 -moz-border-left-colors: #A0A0A0 #FFFFFF !important;
 -moz-border-radius: 0 !important;}

.tabs-container .tabs-newtab-button:hover {
 background: #F0F8FF url("http://img263.imageshack.us/img263/1918/tab2.png") !important;}

/* TAB APPEARANCE */

.tabbrowser-tabs tab {
 background: #E0F0FF url("http://img263.imageshack.us/img263/1918/tab2.png") !important;
 border-style: solid !important;
 border-width: 2px 2px 0 2px !important;
 color: #404040 !important;
 margin-top: 4px !important;
 margin-right: -1px !important;
 padding-top: 2px !important;
 padding-left: 0 !important;
 -moz-border-top-colors: #A0A0A0 #FFFFFF !important;
 -moz-border-right-colors: #A0A0A0 #FFFFFF !important;
 -moz-border-left-colors: #A0A0A0 #FFFFFF !important;
 -moz-border-radius: 0 !important;}

.tabbrowser-tabs tab:hover {
 background: #F0F8FF url("http://img263.imageshack.us/img263/1918/tab2.png") !important;}

.tabbrowser-tabs tab[selected="true"] {
 background: #F0F8FF url("http://img269.imageshack.us/img269/738/tab1w.png") repeat-x !important;
 color: #000000 !important;
 padding-top: 0 !important;
 font-weight: normal !important;
 margin-top: 2px !important;
 -moz-border-radius: 2px 2px 0 0 !important;}

.tabs-bottom {
 background: #F0F8FF !important;}


/* TAB BAR ALIGNMENT */

.tabs-container {
 margin-right: -17px !important;
 margin-left: 1px !important;}

/* TAB BAR BACKGROUND */

#PersonalToolbar {
 -moz-appearance: none !important;
 background: #E0F0FF url("http://img268.imageshack.us/img268/7236/tabbar.png") repeat-x !important;
 border: none !important;}

.tabbrowser-tabs {
 -moz-appearance: none !important;
 background: #DEEEFD !important;
 border: none !important;}

/* TAB BAR HEIGHT */

.tabbrowser-strip {
 height: 32px !important;}

}
