css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#webdeveloper-toolbar {
	border-top: 0 none !important;
	-moz-appearance: none !important;
	background: none !important;
	padding: 0px 0px 0px 0px !important;
    opacity:0.99 !important;
	min-height:24px !important;
	color:white !important;
}

#webdeveloper-toolbar toolbarpalette, #webdeveloper-toolbar toolbaritem, #webdeveloper-toolbar toolbarbutton, #webdeveloper-toolbar hbox, #webdeveloper-toolbar toolbar, #webdeveloper-toolbar toolbox {
	color: black !important;
	background: none !important;
	padding:2px 2px 2px 2px !important;
	-moz-border-radius:0px 0px 0px 0px !important;
	border:0px solid rgba(0,0,0,0) !important;	
	opacity:0.99 !important;
}

#rftc-container button, #rftc-container textbox, #rftc-container label, #rftc-container combobox, #rftc-container combo, #rftc-container select, 
 #rftc-container toolbaritem, #rftc-container menulist, #rftc-container menu, #rfshowbar-button, #rfclosebar-button,
#rfshowbar-toolbarbutton, .rftc-grippy, #rftc-grippy-left, #rftc-grippy-right, #palette-box #rftc-container,  #palette-box .rftc-grippy , 
#palette-box, #rftc-container .toolbarbutton-text, #context-robotoolbar, #rftc-container context {
	color: black !important;
	background: none !important;
	padding:2px 2px 2px 2px !important;
	-moz-border-radius:0px 0px 0px 0px !important;
	border:0px solid rgba(0,0,0,0) !important;	
	opacity:0.99 !important;
}
