css:
@-moz-document regexp("https?:\\/\\/(www\\.)?caf\\.fr.*") {
body {
	background-color: #111 !important;
	color: #fff !important;
}

.bloc-droite-content, #theme-header .navbar-titles-header, .navbar-menu-burger-content-super-pc .navbar, #page-content .page-content-data .page-content-node, #page-content .page-content-data .view-cnaf-actu, .text-center, .carousel-inner, .page-content-data.col-md-12 .pave-services-en-ligne-wrapper .accueil-block-simulateur>div, #allocataire-actu-vdf-paves-accueil .panel-actu-content, #allocataire-actu-paves-accueil .panel-actu-content, .pave-delai-de-traitement, .table-striped>tbody>tr:nth-of-type(odd), #pave_vient_paraitre .panel-article-content, .pave-dernieres-publi {
	background-color: #222 !important;
}

.navbar-menu-burger-content-super-pc a.collapsed, #theme-contenu-menu-wrapper-cnaf > div {
	background-color: #222 !important;
}

.panel, .navbar-menu-burger-content-super-pc .navbar .menu-content-level1 a, .nav>li>a:focus, .nav>li>a:hover, .panel-default .panel-body-white, .pave-dernieres-publi .cadre-dernieres-publi .panel .panel-body {
	background-color: #2d2d2d !important;
}

#theme-content-menu-cnaf a:hover, .pave-dernieres-publi .cadre-dernieres-publi .panel .panel-body:hover {
	background-color: #393939 !important;
}

.block-caf-theme .bloc-droite-content a:not(.btn), .navbar-menu-burger-content-super-pc a, .navbar-menu-burger-content-super-pc a.collapsed, .navbar-menu-burger-content-super-pc .menu-content-level1 .collapsed, .pave-delai-de-traitement .content .delai-intro, .pave-delai-de-traitement .content .delai-txt-icone, .pave-delai-de-traitement .content .a_savoir, .espace-presse-institutionnel .navbar-menu-burger-content-super-pc a.text-uppercase.collapsed, .espace-presse-institutionnel .navbar-menu-burger-content-super-pc .menu-content-level1 .item-content-niv2.collapsed, .espace-partenaires .navbar-menu-burger-content-super-pc a.text-uppercase.collapsed, .espace-partenaires .navbar-menu-burger-content-super-pc .menu-content-level1 .item-content-niv2.collapsed, #pave_vient_paraitre .h3, #pave_vient_paraitre h3, .pave-dernieres-publi .cadre-dernieres-publi .panel .panel-body {
	color: #f2f2f2;
}

.espace-presse-institutionnel #theme-header .navbar-titles-header .active a, .espace-presse-institutionnel #theme-header .navbar-titles-header-mob .active a, .espace-presse-institutionnel .navbar-menu-burger-content-super-pc a.text-uppercase, .espace-presse-institutionnel .page-content-data a:not(.btn) {
	color: #ca4abc;
}

#theme-header .navbar-titles-header .navbar-titles-title a, #theme-header .navbar-titles-header .navbar-ma-caf-header .narvar-ma-caf-name a, .pave-dernieres-publi .cadre-dernieres-publi .panel .panel-body:hover {
	color: #fff;
}

.navbar-menu-burger-content-super-pc .navbar li a {
	border-bottom: 1px solid #0093c4;
}

.panel {
	border: none;
	border-radius: 0px;
}

.collapse {
    border-top: none;
}

.block-caf-theme .bloc-droite-content .h3, .block-caf-theme .bloc-droite-content h3, h3, a.panel-with-arrow .panel-body, .node-pages-vdf-articles .article-resume-VDF, .pave-parcours .cadre-pave-parcours .text-center, #allocataire-actu-vdf-paves-accueil .panel-actu-content .panel-actu-body a:not(.btn), #allocataire-actu-paves-accueil .panel-actu-content .panel-actu-body a:not(.btn) {
	color: #f2f2f2;
}
}

@-moz-document domain("wwwd.caf.fr") {
#theme-contenu-cnaf {
	background-color: #111 !important;
}

#theme-header-links-cnaf, .breadcrumb {
	background-color: #111 !important;
}

#theme-header-links-cnaf .container > .row, #theme-footer-cnaf, #theme-footer-content, .modal-content {
	background-color: #222 !important;
}

#theme-contenu-cnaf .row[class*='conteneur-'][class*='-cnaf'] > [class*='col-'], #theme-contenu-menu-background-cnaf, #theme-contenu-menu-wrapper-cnaf #sidebar-content-cnaf .panel > a.collapsed {
	background-color: #222 !important;
	background-image: none;
	color: #f2f2f2 !important;
}

#theme-contenu-menu-wrapper-cnaf #sidebar-content-cnaf .panel > a.collapsed:hover, #theme-contenu-menu-wrapper-cnaf #sidebar-content-cnaf .sidebar-item > a:hover {
	background-color: #393939 !important;
	color: #f2f2f2 !important;
}

#theme-menu-cnaf .navbar-collapse > ul.nav li.divider-light, #theme-contenu-menu-wrapper-cnaf ul.nav li.divider-light {
	background-color: #0093c4;
}

#theme-contenu-menu-wrapper-cnaf #sidebar-content-cnaf .sidebar-item > a {
	color: #f2f2f2 !important;
}

#theme-header-links-cnaf .navbar-links-cnaf span.navbar-links-item-cnaf:not(.active) a, #theme-header-links-cnaf .navbar-links-access-cnaf span.navbar-links-item-cnaf:not(.active) a, #theme-header-links-cnaf .navbar-macaf-cnaf label, #theme-header-links-cnaf .navbar-macaf-choix-cnaf a, .label-form-cnaf, .titre-bloc-cnaf {
	color: #fff !important;
}

.ouvert-cnaf > a, .ferme-cnaf > a, .titre-bloc-cnaf > a {
	color: #f2f2f2 !important;
}

.ouvert-cnaf > a:hover, .ferme-cnaf > a:hover, .titre-bloc-cnaf > a:hover, .btn-default:hover, .btn-info:hover {
	color: #fff !important;
}



.navbar-menu-burger-content-super-pc a.collapsed, #theme-contenu-menu-wrapper-cnaf > div {
	background-color: #222 !important;
}

.panel, .navbar-menu-burger-content-super-pc .navbar .menu-content-level1 a, .nav>li>a:focus, .nav>li>a:hover {
	background-color: #2d2d2d !important;
}
}
