css:
@-moz-document url-prefix(https://seforums.pantheonmmo.com/) {
.centering {
  max-width: 100%;
}
.layout_ynresponsive1_menu_main {
  max-width: 100%;
}
div#global_content {
  max-width: 100%;
}
.layout_ynresponsive1_menu_main {
  max-width: 100% !important;
  width: 100%;
}
div.layout_page_header .layout_main, div.layout_page_footer .layout_main, div#global_content {
  padding-left: 0;
  padding-right: 0;
}
ul.forum_topics>li.forum_nth_1 {
  background-color: #222;
}
ul.forum_topic_posts>li.forum_nth_1 {
  background-color: #222;
}
ul.forum_topic_posts>li.forum_nth_0,
ul.forum_topics>li.forum_nth_0 {
  background-color: #292929;
}
ul.forum_categories>li>div>h3 {
  background-color: initial;
}
.paginationControl {
  border: none;
}
ul.paginationControl>li>a {
  border: none;
}
.forum_topic_posts_info_body,
.forum_topic_posts_info_body p,
.forum_topic_posts_info_body span{
  font-size:15px !important;
}
.mce-panel,
.form-elements,
#forum_post_quick{
  max-width: 1240px;
  margin:0 auto;
}

#global_content > h2{
  max-width: 1240px;
  margin:0 auto;
}
#header-part {
    border-bottom: 1px solid #777;
}
.navbar-default {
    padding-top: 75px;
    background-color: #111;
    border-color: #111!important;
}
div.generic_layout_container .layout_ynresponsive1_menu_main {
    background-color: #111;
}
.navbar-brand {
  padding: 10px 0 0 20px;
}
div a:hover {
  color: #ea8200;
}
.social a:hover .fa{
  color:#fff !important;   
}

body::-webkit-scrollbar {
width: 15px; 
}

body::-webkit-scrollbar-track {
background: #222;
}

body::-webkit-scrollbar-thumb {
background-color: #ffc600;   
border-radius: 3px; 
border:1px solid #222;
}

:root {
    --pan-yellow: #ffc600;
    --pan-white: #eee;
    --pan-blue: #7cd1ec;
    --pan-pink: #ea3a85;
}


}
