css:
@-moz-document url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com")
{
/* i really want this to be global */
 
 #header {
    background-color: #8a89b6;
}

.dark-theme .rbx-header {
    background-color: #ffffff00;
    border: solid #00000000;
    border-width: 0 0 1px;
}
  .dialog-container .dialog-header

                             
  .navigation .notification-icon, .rbx-highlight
  {
    background: #6f6e93;
  }

  #chat-header
  {
    background: #6f6e93;
      /*Party header*/
    ;
  }

 
  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.imgur.com/H2yeqkW.png) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

.dark-theme .section-content {
    background-color: #908cbd00;
    color: #fff;
}
.dark-theme .rbx-left-col {
    box-shadow: 0 0 8px 0 rgb(0 0 0 / 67%);
    background-color: #8987b500;
}.dark-theme #btr-blogfeed .btr-feed {
    color: #ffffff;
}.dark-theme .rbx-tabs-horizontal .nav-tabs {
    background-color: #ecbbd300;
    color: #fff;
}.dark-theme .rbx-tab .rbx-tab-heading {
    background-color: #e1bcd538;
    color: #fff;
}.dark-theme .text, .dark-theme .text-date-hint, .dark-theme .text-default, .dark-theme .text-description, .dark-theme .text-footer, .dark-theme .text-info, .dark-theme .text-label, .dark-theme .text-secondary, .dark-theme p {
    color: #ffffff;
}.dark-theme .profile-avatar-right .profile-avatar-mask {
    background-color: #393b3d00;
    color: #fff;
}.profile-avatar-left .enable-three-dee {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    border: 1px solid transparent;
    background-color: #fff0;
    border-color: #ffffff;
    color: #ffffff;
    cursor: pointer;
    display: inline-block;
    font-weight: 500;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 16px;
    line-height: 100%;
    border-radius: 0px;
    border: 1px solid #ffffff;
    position: absolute;
    top: 12px;
    right: 12px;
    min-height: 32px;
}.dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img {
    background-color: #8886b3;
}.dark-theme .layer {
    background-color: #8c89ba69;
}.dark-theme .asset-thumb-container {
    background-color: #8c89ba69;
    border: 0;
}.dark-theme .slide-switcher.groups {
    background-color: #8c89ba69;
    color: #fff;
}.dark-theme .slide-switcher .slide-item-container .slide-item-container-right {
    background-color: #8c89ba69;
    color: #fff;
}
.dark-theme .slide-switcher .slide-item-container .slide-item-container-right {
    background-color: #8c89ba69;
    color: #fff;
}.dark-theme {
    background-color: #c7cdd400;
    color: #fff;
}.dark-theme .slide-switcher .slide-item-container .slide-item-container-left {
    background-color: #8c89ba69;
}.dark-theme .slide-switcher.groups {
    background-color: #8c89ba69;
    color: #fff;
}
.dark-theme .border, .dark-theme .border-bottom, .dark-theme .border-left, .dark-theme .border-right, .dark-theme .border-top {
    border-color: #ffffff;
}.dark-theme .abp .BadAdButton:hover, .dark-theme .text-alert, .dark-theme .text-error, .light-theme .abp .BadAdButton:hover, .light-theme .text-alert, .light-theme .text-error {
    color: #fcd2e9;
}



.dark-theme .input-group .input-field {
    background-color: rgb(57 57 78);
    border-color: hsl(0deg 0% 100% / 0%);
    color: #ffffff;
    border-radius: 8px;
}.dark-theme .rbx-header .navbar-search .navbar-search-option a {
    color: #ffffff;
}.dark-theme .rbx-header .navbar-search .new-dropdown-menu {
    margin-top: 6px;
    left: -6px;
    width: calc(100% + 12px);
    background-color: #5a5a80;
    box-shadow: 0 4px 16px 0 rgb(0 0 0 / 52%);
}.dark-theme .rbx-header .navbar-search .navbar-search-option .new-navbar-search-anchor .navbar-list-option-suffix {
    color: #ffffff;
}.dark-theme .slide-switcher.games {
    background-color: #a2a9af00;
    color: #fff;
}


.dark-theme .item-card-container .item-card-thumb-container {
    border: 0;
    background-color: rgb(57 59 61 / 0%);
    border-color: #393b3d00;
}.dark-theme .rbx-tabs-horizontal #horizontal-tabs .rbx-tab:not(.active) a:not(:hover) {
    background-color: #6a698c;
    color: #fff;
}.dark-theme .rbx-tabs-horizontal .nav-tabs {
    background-color: #71709321;
    color: #fff;
}


.dark-theme .avatar-card-container {
    background-color: #8a89b6;
    color: #fff;
}.dark-theme .avatar-card-btns {
    background-color: #f4f9ff00;
    color: #fff;
}.dark-theme .btn-secondary-md {
    background-color: transparent;
    border-color: #ffffff;
    color: hsl(0deg 0% 100%);
    border-radius: 0px;
}.dark-theme .avatar-card-btns .accept-friend {
    background-color: #ffffff75;
    border-color: #fff;
    color: #000000;
    border-radius: 0px;
}



.dark-theme #buy-robux-popover-menu li a, .dark-theme #settings-popover-menu li a {
    color: #ffffff;
}.dark-theme .dropdown-menu {
    background-color: #4e4e5c;
    color: #fff;
}.dark-theme .menu-vertical {
    background-color: #4e4e5c;
    color: #fff;
}.dark-theme .menu-vertical .menu-option {
    color: #ffffff;
}.dark-theme .input-field {
    background-color: rgb(78 78 92);
    border-color: hsla(0,0%,100%,.2);
    color: #ffffff;
    border-radius: 1px;
}}
.dark-theme a.btn-common-play-game-lg {
    background-color: #616080;
    border-color: #ffffff;
    color: #fff;
    border-radius: 0px;
}.dark-theme .touch a.btn-common-play-game-lg.active, .dark-theme .touch a.btn-common-play-game-lg:active, .dark-theme .touch a.btn-common-play-game-lg:focus, .dark-theme .touch a.btn-common-play-game-lg:hover, .dark-theme .touch a.btn-common-play-game-lg:visited, .light-theme .touch a.btn-common-play-game-lg.active, .light-theme .touch a.btn-common-play-game-lg:active, .light-theme .touch a.btn-common-play-game-lg:focus, .light-theme .touch a.btn-common-play-game-lg:hover, .light-theme .touch a.btn-common-play-game-lg:visited {
    color: #fff;
    background-color: #48475e52;
    border-color: #ffffff;
}.dark-theme .stack .stack-list .stack-row {
    background-color: #747399;
    color: #fff;
}.dark-theme .container-empty, .dark-theme .section-content-off {
    background-color: #232527;
    color: #ffffff;
}

.btr-profile .btr-status-online {
    color: #fff4fb;
}

  .avatar-back
  {
    background-image: url(https://drive.google.com/file/d/1oE53p4dnJDHjrA25SKP0j3OzxgaxYA1V/view?usp=sharing);
 }
