css:
@-moz-document url-prefix("http://rasp.sstu.ru/") {
body {
    background: #252525;
    color: #999999;
}

.page {
    background: #252525;
}

.text-center {
    background: #252525;
    color: #999999;
}

.list-group-item.list-group-item-default.pseudo {
    background: #373B3E;
}

.panel-body {
    background: #252525;
}

.panel-title {
    background: #252525;
    color: #999999;
}

a {
    color: #3296C8;
}
span.pseudo {
    color: #3296C8;
}

.panel-default > .panel-heading  { 
    background: #373B3E;
}

.panel-heading  { 
    background: #373B3E;
}

.panel-heading  h3  { 
    background: #373B3E;
}

h3.panel-title  { 
    background: #373B3E;
}

h4 {
    background: #373B3E;
}

.col-sm-3 {
    background: #252525;
}

.row {
   background: #252525; 
}

.today {
    background: rgba(255, 255, 235, 0.1);
}

.rasp-table-inner-cell.today {
    background: #999999;
}

.rasp-table-inner-cell {
    color: #FFFFFF;
}

.chet {
    border-color: rgba(255, 255, 235, 0.1);;
}

.nechet {
    border-color: rgba(255, 255, 235, 0.1);;
}

.chet .rasp-table-row-header {
    background-color: #373B3E;
}

.chet .rasp-table-col-first {
    background-color: #373B3E;
}

.nechet .rasp-table-row-header {
    background-color: #373B3E;
}

.nechet .rasp-table-col-first {
    background-color: #373B3E;
}

.logo-title {
	color: #FFFFFF;
    font-size: 16px;
}

img {
    display: none;
}

.h2 {
    color: #FFFFFF;
}
}
