css:
@-moz-document domain("color-hex.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #777;
        }

.content {
	background: #111;
}
.navbar {
    background-color: #151515;
    background-image: linear-gradient(to bottom, #111, #222);
    border-bottom-color: #444;
}
html {
	background: #111;
}
body {
	color: #ccc;
	background: #111;
}
.navbar-default {
	border-color: #272727;
	background: #181818;
}
[src='/logo@2x.png'] {
	filter: invert(1);
}
#breadcrumb, .table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td, .preview {
	border-color: #333;
}
.colordivbig {
	border-color: #222;
}
.nav>li>a:hover, .nav>li>a:focus {
	background: #222;
}
.colorrow {
	background: #222;
	border-color: #444;
}
.panel-success>.panel-heading {
    color: #6bff6e;
    background-color: #3f483b;
    border-color: #363e2f;
}
.panel-success {
	border-color: #363e2f;
}
.panel {
	background: #111;
}
pre {
	background: #151515;
	border-color: #444;
	color: #ccc;
}
.well {
	background: #151515;
	border-color: #232323;
}
.commentcontainer .date {
	color: #888;
}
.footer {
	background: #151515;
	color: #777;
	border-color: #252525;
}
.navbar-default .navbar-nav>li>a:hover, .navbar-default .navbar-nav>li>a:focus {
	color: #ccc;
}
.navbar-default .navbar-nav > li > a {
    color: #fff;
    text-shadow: 1px 1px 1px #111;
}
.btn-default {
	background: #111;
	color: #ccc;
	border-color: #444;
}
.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open>.dropdown-toggle.btn-default {
	background: #262626;
	border-color: #6d6d6d;
	color: #ccc;
}
.form-control {
	background: #111;
	color: #aaa;
	border-color: #444;
}
.preview.bgblack > p:after, .preview.bgwhite > p:after {
	content: 'Jebeć ciebie, twą ekipe, lache ciągnie twoja matka.';
	visibility: visible;
	margin-left: -175px;
}
.preview.bgblack > p, .preview.bgwhite > p {
	visibility: hidden;
}
hr {
	border-color: #222;
}
.colordvcon a, .colordvconline a, .favcoms a, .palettecontainerlist a {
	color: #eee;
}
.userfrm {
	background: #151515;
	border-color: #444;
}
[src='../img/loading.gif'] {
	filter: invert(.93);
}
.alert-warning {
    color: #d2a65a;
    background-color: #1c190a;
    border-color: #1a150b;
}
.alert-success {
    color: #5cff5f;
    background-color: #2f4227;
    border-color: #364827;
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
	background: #222;
}
input {
	background: #111;
	border: 1px solid #333;
	color: #ccc;
}
.palettecolordivcon {
	border-color: #333;
}
.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
	background: #222;
	border-color: #333;
}
.pagination>li>a, .pagination>li>span {
	background: #111;
	border-color: #333;
}
.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
	background: #111;
	border-color: #333;
	color: #888;
}
.colordvcon:hover, .palettecontainerlist:hover {
    box-shadow: 0 1px 2px rgba(255, 255, 255, .7);
}
.blog time {
	color: #888;
}
}
