css:
@-moz-document url-prefix("https://rarbg.to/torrents.php"), url-prefix("https://rarbg.to/torrent") {
/* Sets the base font family and size */
body,
p,
table,
td.lista,
#searchinput,
.black,
.lista-rounded > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) {
    font-family: "/*[[font]]*/" !important;
    font-size: /*[[font_size]]*/pt !important;
    word-wrap: break-word !important;
}

/* Sets the font size for categories section */
tr.lista2 span:nth-child(3),
div.divadvscat > a:nth-child(2),
.divadvclearcats {
    font-size: /*[[font_size]]*/pt !important;
}

/* Sets the font size for three main columns */
tr.lista2 td a,
tr.lista2 > td:nth-child(3),
tr.lista2 > td:nth-child(4) {
    font-size: /*[[font_size]]*/pt !important;
}

.lista-rounded {
    background-color: #e6e6ea !important;
}

/* Removes some padding */
body > table:nth-child(6) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2),
table {
    padding-top: 0px !important;
}

.lista-rounded {
    background-color: #e6e6ea !important;
}

/* Hides many page elements */
body > div:nth-child(7),
body > div:nth-child(9),
body > div:nth-child(12),
img[src*="cat_new1.gif"],
img[src*="cat_new2.gif"],
img[src*="cat_new3.gif"],
img[src*="cat_new4.gif"],
img[src*="cat_new5.gif"],
img[src*="cat_new6.gif"],
img[src*="cat_new7.gif"],
img[src*="cat_new8.gif"],
img[src*="cat_new9.gif"],
img[src*="cat_new10.gif"],
td.header6:nth-child(1),
table > tr > td:nth-child(1),
table:nth-child(6) > tbody > tr > td:last-child > div.content-rounded > table.lista-rounded > tbody > tr:nth-child(2) > td > table.lista2t:last-child > tbody > tr.lista2 > td.lista:first-child,
table:nth-child(6) > tbody > tr > td:first-child,
body > table:nth-child(4) {
    display: none !important;
}

.lista-rounded > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(2) > img:nth-child(1),
#description > a:nth-child(2) > img:nth-child(1) {
    width: 550px !important;
}

/* Sets the width of two columns */
tr.lista2 > td:nth-child(3) {
    width: 75px !important;
}
tr.lista2 > td:nth-child(4) {
    width: 65px !important;
}

/* Hides pointless columns */
.lista3 > tbody:nth-child(1) > tr:nth-child(1),
.lista-rounded > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > br:nth-child(2),
.lista-rounded > tbody:nth-child(1) > tr:nth-child(3) > td:nth-child(1) > br:nth-child(3),
.lista-rounded > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > br:nth-child(4),
.lista-rounded > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > br:nth-child(5),
.lista-rounded > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > br:nth-child(2),
tr.lista2 > td:nth-child(2) > span:nth-child(3),
body > br:nth-child(8),
body > br:nth-child(11) {
    display: none !important;
}

/* Hides Columns in table */
td.header6:nth-child(5),
tr.lista2 > td:nth-child(5),
td.header6:nth-child(6),
tr.lista2 > td:nth-child(6),
td.header6:nth-child(7),
tr.lista2 > td:nth-child(7),
td.header6:nth-child(8),
tr.lista2 > td:nth-child(8) {
    display: none !important;
}

/* Adds padding to the datetime column */
tr.lista2 > td:nth-child(2) {
    padding-left: 10px !important;
}

/* Makes visited links green */
td a:visited {
    color: /*[[visited_links_colour]]*/ !important;
}

/* Makes some text not bold */
a,
h1 {
    font-weight: normal !important;
}

}
