css:
@-moz-document domain("talkspirit.com") {
    ul {
        padding: 0 !important;
        border-radius: 0 !important;
        margin: 0 !important;
        border: 0 !important;
    }

    ul li {
        padding: 0 !important;
        border-radius: 0 !important;
        margin: 0 !important;
        border: 0 !important;
    }

    .chat-conversation__sublist-item__user-name {
        margin: 0 !important;
    }

    .chat-message__text {
        border-radius: 0 !important;
        padding: 1px !important;
    }

    div {
        margin: 1px !important;
        padding: 0 !important;
        border: 0 !important;
    }

    .chat-conversation__date {
        padding: 0;
        border-radius: 0;
    }

    .post__card__header {
        padding: 0;
    }

    .post__card__comment__list__header {
        padding: 1px;
    }

    .post__section {
        width: 100% !important;
    }

    .post__card {
        margin-bottom: 2px !important;
    }

    .post__footer {
        padding: 1px;
    }

    .item-preview__image {
        display: none;
    }
}
