@-moz-document domain("www.vim.org"),
               domain("vim.sourceforge.net"),
               domain("vim.sf.net"),
               url-prefix("http://www.google.com/cse?cx=partner-pub-3005259998294962%3Abvyni59kjr1")
{

 /* Basic Vim logo color: #007F00 */

   body
     {
         background : #0A1A07;
              color : #97BF97  !Important;
     }
   table[cellspacing="0"][cellpadding="0"][border="0"] td
     {
         background : #0A1A07  !Important;
              color : #58661C  !Important;
         font-size  : larger   !Important;
         font-family: "minion web", monaco, sans-serif !Important;
     }

   table[cellspacing="0"][cellpadding="0"][border="0"] td a:link
     {
         color : #CE5C00 !important;
     }

   table[cellspacing="0"][cellpadding="0"][border="0"] td a:visited
     {
         color : #F57900 !important;
     }

   body, td, p
     { 
	font-family: arial,helvetica,sans-serif;
     }

     th
      {
         font-family: arial,helvetica,sans-serif;
         font-weight: bold;
         text-align: left;
      }

     em
      {
    font-weight: bold;
    font-style: italic;
      }

.darkbg {
    background-color: #007f00;
}

td.lightbg {
    background-color: #000;
    padding: 17px;
}

.sidebar {
    font-family: arial,helvetica,sans-serif;
    background-color: #f0f0dd;
    font-size: small;
}

.topheader {
    font-family: arial,helvetica,sans-serif;
    font-size: large;
    font-weight: bold;
    background-color: #cccc99;
}

   table[width="180"][cellspacing="0"][cellpadding="0"]
     {
         width : 40px !Important;
     max-width :  134px !Important;
        border : thin dotted #ccffcc !Important;
     }

   table[width="172"][cellspacing="0"][cellpadding="0"][border="0"]
     {
         display: none !Important;
     }

 .newsdate {
     font-style: italic;
     color: #6666cc;
     font-size:small;
}

    pre, code
     {
         font-family :
           "ubuntu mono", "9x15", "andale mono", "monotype.com", "courier new", monospace;
     }


/*  Headings  */
     h1 {
         font-family: "minion web",arial,helvetica,sans-serif;
         font-size: 210% !Important;
         font-weight: bold;
         color : #395523 !important;
     }

.txth1 {
    font-family: arial,helvetica,sans-serif;
    font-size: 170% !Important;
    max-width: 30%;
    font-weight: normal;
    background-color : #022600 !Important;
    color: #09AB00 !Important;
}

h2 {
    font-family: arial,helvetica,sans-serif;
    font-size: 100%;
    font-weight: bold;
}

.txth2 {
    background-color : #022600 !Important;
    color: #09AB00 !Important;
    font-size: 150%;
    font-weight: bold;
}

h3 {
    font-family: arial,helvetica,sans-serif;
    font-size: small;
    font-weight: bold;
}

.txth3 {
    background-color : #022600 !Important;
    color: #09AB00 !Important;
    font-size: small;
    font-weight: bold;
}
/*  end Headings  */


small {
    font-family: arial,helvetica,sans-serif;
    font-size: 75%;
}

a.small {
    font-family: arial,helvetica,sans-serif;
    font-size: 75%;
    text-decoration: none;
}

.style1 {
    color: #666666
}

input {
    font-family: arial,helvetica,sans-serif;
}

textarea {
    font-family: arial, helvetica, sans-serif; 
    width: 100%; 
}

.prompt {
    font-family: arial,helvetica,sans-serif;
    font-weight: bold;
}

.errortext {
    font-family: arial,helvetica,sans-serif;
    color: #cc0000
}

.errorheader
{
    font-family: arial,helvetica,sans-serif;
    color: #cc0000;
    font-size: medium;
 }

 table[width="100%"][cellspacing="2"][cellpadding="4"][border="0"]
 {
     background-color : #080213;
}

.tableheader {
    font-family: arial,helvetica,sans-serif;
    font-weight: bold;
    background-color: #1C072E;
    border: thin solid #aaaaaa;
}

.rowodd {
    font-family: arial,helvetica,sans-serif;
    background-color: #F0F0DD;
}

.roweven {
    font-family: arial,helvetica,sans-serif;
    background-color: #CCCC99;
}

   tr.tableheader th , tr.tableheader th a
    {
	font-family: arial,helvetica,sans-serif;
	background-color: #1C072E !Important;
	text-align: center;
        vertical-align : bottom;
    }


   td.checker
    {
	background-color: #007f00;
	background-image: url(http://vim.sourceforge.net/images/checkerboard.gif);
    }

  table[bordercolor="red"][cellspacing="0"][cellpadding="0"] a:link ,
  table[bordercolor="red"][cellspacing="0"][cellpadding="0"] a:visited
   {
        color : #3FA300 !Important;
        
   }


/* This is the vim.org "sidebar" navigation box. */
   body > table:nth-of-type(0n+2) td.sidebar
   {
	   font-family : arial,helvetica,sans-serif;
         font-size : small;
         max-width : 8% !important;
   }

  body > table:nth-of-type(0n+2) td.sidebar >table tr td.sidebar
  {
           color : #FFF !important;
      background : -moz-linear-gradient(left,  #1f3b08 0%, #627d4d 86%) !Important; /* FF3.6+ */
  }

  body td.sidebar > table td
  {
      padding-left : 3px !important;
  }
  
  body > table:nth-of-type(0n+2) td.sidebar + td
    {
         /* placeholder */
    }

  span.sidebarheader  {  font-size : larger !important; }
  td.sidebarheader a
   {
	   font-family : arial,helvetica,sans-serif;
	     font-size : small;
             color : inherit !Important;
        text-decoration : none !Important;
   }
  input[name="q"],
  input[size="20"] /* name="q" size="20" type="text" */
    {
         width : 100% !important;
    }

} /* EXIT -moz-document !!!  */


@-moz-document url-prefix("http://www.vim.org/scripts/"),
              url-prefix("http://vim.sourceforge.net/scripts/"),
              url-prefix("http://vim.sf.net/scripts/")
{

    body >table:first-child
      {
            display: none !Important;
      }

   table[cellspacing="0"][cellpadding="0"][border="0"] > tbody tr:nth-child(0n+8) td br
     { line-height  : 0px !important; }
   table[cellspacing="0"][cellpadding="0"][border="0"] > tbody > tr:nth-child(0n+8) td
     {
        white-space : pre-wrap !important;    
         background : #161A07  !Important;
              color : #97BF97  !Important;
         font-size  : smaller  !Important;
       font-family  : "envy code R", "consolas", "inconsolata", monospaced !important;
            padding : 0.4ex 0ex 0.2ex 0.4ex !important;
     }


   p table[border="0"][cellpadding="4"][cellspacing="2"]
     {  /* width="100%"> */
         width : 100% !important;
     }
     
   p table[border="0"][cellpadding="4"][cellspacing="2"]>tbody tr td:last-of-type
     {
         font-family : "minion web pro", ubuntu, sans-serif !important;
           font-size : small !important;
     }
} /* EXIT -moz-document !!!  */

