css:
@-moz-document url-prefix("https://dante.iis.p.lodz.pl") {
body {
    /*background: radial-gradient(#202020, black 100%);*/
    background: #101010;
    color: silver;
}

/*::-webkit-scrollbar {
    display: none;
}*/

h1, h5 {
    color: silver;
}

div.alert.alert-warning{
    display: none;
}

div#taskwindow pre {
    background-color: black;
    border: 1px solid #606060;
    color: silver;
    border-radius: 0px;
}

div#taskwindow pre span {
    color: #d68711 !important;
}

.lang-cpp.editor-colors div {
    background-color: transparent !important;
}

p {
    color: silver;
}

.btn.btn-primary {
    /*background: radial-gradient(
    circle farthest-corner at center center,
    #606060 10%,
    black
  );
    border-color: #606060;*/
    background: #606060;
    border-color: silver;
}

.btn-primary:not(:disabled):not(.disabled):active,
.btn-primary:not(:disabled):not(.disabled):active,
.btn-primary:not(:disabled):not(.disabled).active,
.btn-primary:not(:disabled):not(.disabled):active,
.show > .btn-primary.dropdown-toggle {
    background: silver;
    border-color: white;
    color: black;
}

.btn:focus,
.btn-primary:focus,
.btn-primary:not(:disabled):not(.disabled):active:focus,
primary.dropdown-toggle:focus,
.btn-primary:not(:disabled):not(.disabled).active:focus,
.btn-primary:not(:disabled):not(.disabled):active:focus,
.show > .btn-primary.dropdown-toggle:focus {
    box-shadow: none;
}

div.cold-md-1,
div.col-md-6,
div.col-md-12 {
    color: silver;
}

a {
    color: #d68711;
}

a:hover {
    color: white;
}

.breadcrumb {
    background: transparent;
}

ol.mb-1.breadcrumb.d-flex.px-3.py-1.align-items-center a {
    color: silver;
}

ol.mb-1.breadcrumb.d-flex.px-3.py-1.align-items-center a:hover {
    color: white;
}

a.nav-link {
    color: silver;
}

a.nav-link:hover {
    color: white;
}

.breadcrumb button,
.breadcrumb a.btn {
    display: none;
}

.navbar-expand-sm {
    display: none;
}

span.badge.badge-danger {
    background: radial-gradient( circle farthest-corner at top center,
    red,
    black 130%);
    padding: 6px;
}

.dropbox[data-v-1ee98239] {
    background-color: silver;
    border-radius: 10px;
}

textarea.form-control {
    background-color: silver;
    border-radius: 10px;
}

textarea.form-control::-webkit-input-placeholder {
    color: #495057;
}

.form-control:focus {
    border-color: silver;
    box-shadow: none;
}

i.fa.fa-star-half-alt,
.fas.fa-star,
.far.fa-star {
    color: gold;
}

.bg-light,
th.text-center {
    background: #101010 !important;
}

.nav-tabs .nav-item.show .nav-link,
.nav-tabs .nav-link.active {
    background-color: silver;
}

.card-body {
    background-color: silver;
    color: #495057;
    border-radius: 10px;
}

.card-body p {
    color: #495057;
}

.card {
    background: transparent;
}

.text-danger {
    font-weight: bold;
}

p.small.text-muted {
    padding: 10px;
}

.dropbox[data-v-1ee98239],
.dropbox[data-v-1ee98239]:hover {
    background-color: black;
    color: silver;
    border: 1px solid silver;
    border-radius: 0px;
}

#btn-chat,
.btn-warning {
    background: #d68711;
    border: none;
    color: #101010;
    font-weight: bold;
}

.btn-info { /* guzik do rozwijania zada≈Ñ */
    display: none;
}

/*.btn-info, .btn-info:hover{
    background: #d68711;
    border: none;
    color: #101010;
    font-weight: bold;
}

.btn-info:not(:disabled):not(.disabled):active{
    background: #f2ae49;
    color: #101010;
}

.btn-info:not(:disabled):not(.disabled):active:focus{
    box-shadow: 0 0 0 0.2rem#f2ae49;
}*/

.btn-secondary {
    background: #d68711;
    border: none;
    color: #101010;
    font-weight: bold;
}


hr {
    padding-top: 5px;
    border: transparent;
}

.alert-success {
    color: #155724;
    background-color: #90EE90;
    border: 1px solid #155724;
}

p.mb-0 {
    color: #155724;
}

div .modal-header,
.modal-body,
.modal-footer {
    background: #101010;
}

.text-underline {
    text-decoration-line: none;
}

.modal-header {
    border-top-right-radius: 0;
}

.btn-danger {
    background: radial-gradient( circle farthest-corner at top center,
    red,
    black 130%);
    border: none;
    font-weight: bold;
}

.progress {
    background-color: transparent;
}

.progress[data-v-5f88d24b] {
    background-color: silver;
}

.bg-info {
    /*background-color: #d68711 !important;*/
    background: linear-gradient(90deg, red 0%, yellow 15%, lime 30%, cyan 50%, blue 65%, magenta 80%, red 100%);
    background-size: 200%;
    animation: moveGradient 4s linear infinite;
    text-shadow: 0 0 2px black, 0 0 2px black, 0 0 2px black, 0 0 2px black;
}

@keyframes moveGradient {
    0% {
        background-position: 0% 0%
    }
    100% {
        background-position: -200% 0%
    }
}
}

@-moz-document url-prefix("https://pp.iis.p.lodz.pl") {
body {
    background: #101010;
    color: silver;
}

div#sec10-body table, div#sec6-body table{
    background-color: black !important;
    border: 1px solid silver;
}

table.auto-style1 .a2{
    color: silver !important;
}

th{
    background: #101010;
    color: silver;
}

div#sec1-body span{
     color: #d68711 !important;
}

pre{
    background: black;
    color: silver;
    border: 1px solid silver;
    padding: 10px;
}

a{
    color: #d68711 !important;
}

pre.console{
    background: black;
}

pre.console span.stdout{
    background: black;
    color: silver !important;
}

td .a1, .a2, .a3, div, .mhdr{
    background: 	#101010 !important;
}

td.console{
    background: black;
    color: silver;
}

span.stdin{
    background: #d68711;
    color: white;
    padding: 1px;
}

span.CR{
    color: white;
}

.code{
    background: #101010;
    color: silver;
}

span.cmd-text{
    color: #d68711;
}

pre.sourceCode{
    background: #101010;
}

span .dv, .fl, .ot, .nb, .mi{
    color: #F78C6C !important;
}

span .dt, .cf, .kw, .k, .kt, .keyword{
    color: #C792EA !important;
}

span .sc, .st, .im, .string_literal{
    color: #C3E88D !important;
}

span .co, .comment{
    color: gray !important;
}

span .pp, .cp{
    color: #82AAFF !important;
}

span.o, .cmd-args, .cpf{
    color: silver !important;
}

span.s{
    background:#101010 !important;
}

span.hll{
    background:#dfdf09 !important;
    padding: 0px;
}

span.error2{
    background-color: #FF000090 !important;
    color: white;
}

table.msgT, .macro, .msgEvent{
    color: black;
    background:	#dfdf09 !important;
    padding: 2px;
    border-radius: 5px;
}

.mrange{
    background: none;
    border-bottom: none;
}

.PathIndex, a.sourceLine{
    color: silver !important;
}

a.errorLine, td.error, span.stderr{
    background-color: #FF000090;
    color: white !important;
    padding: 2px;
}

td.num{
    background-color: #101010;
    color: silver;
}
}
