css:
@-moz-document domain("usaco.org") {
  body,
  td,
  th {
    font-family: Microsoft YaHei;
    font-size: 15px;
    line-height: 18pt;
    color: #000;
  }

  #choose-language {
    border-radius: 10px;
    padding: 5px 8px;
    border-style: none;
    transition: all 0.15s ease;
  }

  p {
    text-indent: 30px;
  }
  h2 {
    text-indent: 0px;
  }

  h1 {
    font-size: large;
  }

  img {
    border-radius: 5px;
    border-style: none;
  }

  a:link {
    color: #5e72e4;
    font-weight: bold;
    text-decoration: none;
  }
  a:active {
    color: #5e72e4;
    font-weight: bold;
    text-decoration: none;
  }
  a:visited {
    color: #5e72e4;
    font-weight: bold;
    text-decoration: none;
  }
  a:hover {
    color: #5e72e4;
    font-weight: bold;
    text-decoration: underline;
    text-indent: 0px;
  }

  input {
    border-radius: 10px;
    background: #5e72e4;
    padding: 5px 8px;
    border-style: none;
    color: white;
    transition: all 0.15s ease;
  }

  button {
    border-radius: 10px;
    background: #5e72e4;
    padding: 5px 8px;
    border-style: none;
    color: white;
    transition: all 0.15s ease;
  }

  pre {
    text-indent: 0pt;
  }

  pre.prettyprint {
    font-family: Fira Code;
    text-indent: 0px;
    border-radius: 10px;
    background: #f8f8f8;
    padding: 5px 8px;
    border-style: none;
    color: black;
    transition: all 0.15s ease;
  }
  body {
    text-indent: 30px;
    margin-top: 0px;
    margin-bottom: 0px;
    margin-left: 15%;
    margin-right: 15%;
    background-color: #ffffff;
    border-style: none;
    line-height: 24pt;
  }

  div {
    border-radius: 5px;
    text-indent: 0px;
    border-style: none;
  }
  div.prob-in-spec {
    text-indent: 30pt;
  }
  div.main {
    background-color: #ffffff;
    width: 914px;
    text-align: left;
    border-radius: 5px;
    border-style: none;
  }

  .content {
    border-radius: 5px;
    border-style: none;
  }

  .navbar {
    position: relative;
    top: -60px;
    border-radius: 5px;
    border-style: none;
  }

  .form_error {
    font-weight: bold;
    color: #f80031;
    padding-top: 5px;
    border-radius: 5px;
    border-style: none;
  }
  .navbar ul {
    border-radius: 5px;
    border-style: none;
    text-indent: 0px;
    list-style: none;
    margin: 20px;
    padding: 0;
  }
  .navbar li a {
    display: inline;
    font-variant: small-caps;
    font-size: 15px;
    background-color: #5e72e4;
    color: #ffffff;
    text-decoration: none;
    text-align: center;
  }
  .navbar li a:hover {
    background-color: #5e72e4;
    color: #fff;
    text-indent: 0px;
    text-decoration: none;
    font-weight: bold;
  }
  .shadow1 {
    margin: 0px;
    width: 900px;
    background-color: white; /* Needed for IEs */
    -moz-box-shadow: 5px 5px 5px white;
    -webkit-box-shadow: 5px 5px 5px white;
    box-shadow: 5px 5px 5px white;
    zoom: 1;
    border-radius: 5px;
    border-style: none;
  }
  .shadow1 .content {
    position: relative; /* This protects the inner element from being blurred */
    width: 900px;
    background-color: #fff;
    border-radius: 5px;
    border-style: none;
  }
  .panel {
    text-indent: 30px;
    background-color: #fff;
    padding: 5px 15px 10px 15px;
    text-align: left;
    border-radius: 5px;
    border-style: none;
  }
  .panel a:link {
    color: #5e72e4;
    text-indent: 0px;
    text-decoration: none;
    font-weight: bold;
  }
  .panel a:active {
    color: #5e72e4;
    text-indent: 0px;
    text-decoration: none;
    font-weight: bold;
  }
  .panel a:visited {
    color: #5e72e4;
    text-decoration: none;
    font-weight: bold;
  }
  .panel a:hover {
    color: #5e72e4;
    text-decoration: underline;
    font-weight: bold;
  }
  .panel h2 {
    font-variant: small-caps;
    line-height: 25%;
  }
  .sponsors span {
    display: inline-block;
    border-radius: 5px;
    border-style: none;
  }
  .sponsors a {
    vertical-align: text-bottom;
    padding: 1px;
    text-decoration: none;
    text-align: center;
    float: left;
    border-radius: 5px;
    border-style: none;
  }
  .sponsors a img {
    border-radius: 5px;
    border-style: none;
  }
  .sponsors a:hover {
    border-radius: 5px;
    border-style: none;
    text-decoration: none;
  }

  .panel li {
    text-indent: 0px;
    padding-top: 5px;
  }

  .field label {
    display: inline-block;
    width: 80px;
    font-weight: bold;
  }

  .field2 {
    padding-top: 5px;
    border-radius: 5px;
    border-style: none;
  }
  .field2 label {
    display: inline-block;
    width: 150px;
    font-weight: bold;
  }
  .field2 select {
    border-radius: 10px;
    background: #5e72e4;
    padding: 5px 8px;
    border-style: none;
    color: white;
    transition: all 0.15s ease;
  }

  .forgotpass label {
    display: inline-block;
    width: 80px;
  }
  .forgotpass {
    padding-top: 0px;
    font-size: 10px;
  }
  .field {
    padding-top: 5px;
  }
  .historypanel {
    text-decoration: none;
    border-radius: 5px;
    border-style: none;
    color: #000;
  }
  .historypanel img {
    border-radius: 5px;
    border-style: none;
  }

  .historypanel:hover {
    background-color: #ffffff;
    border-radius: 5px;
    border-style: none;
  }

  .problem-text {
    text-indent: 30px;
    background: white;
    line-height: 18pt;
    border-radius: 5px;
    border-style: none;
    padding-right: 20px;
    padding-left: 20px;
    padding-top: 5px;
    padding-bottom: 10px;
    margin-bottom: 15px;
    font-family: Microsoft YaHei;
  }
  .problem-text h4 {
    text-indent: 0px;
  }

  .problem-text pre.in {
    font-family: Fira Code;
    text-indent: 0px;
    border-radius: 10px;
    background: #f8f8f8;
    padding: 5px 8px;
    border-style: none;
    color: black;
    transition: all 0.15s ease;
  }

  .problem-text pre.out {
    font-family: Fira Code;
    text-indent: 0px;
    border-radius: 10px;
    background: #f8f8f8;
    padding: 5px 8px;
    border-style: none;
    color: black;
    transition: all 0.15s ease;
  }

  pre,
  code,
  .mono,
  .mono * {
    font-family: Fira Code !important;
  }

  .prewrap {
    white-space: pre-wrap;
  }

  .status-working {
    border-style: none;
    border-radius: 5px;
    font-weight: bold;
  }

  .status-yes {
    font-weight: bold;
    border-radius: 5px;
    border-style: none;
    color: #5e72e4;
  }

  .status-no {
    font-weight: bold;
    border-radius: 5px;
    border-style: none;
    color: #f80031;
  }

  .trial-result {
    border-radius: 5px;
    border-style: none;
    display: inline-block;
    height: 50px;
    margin: 3px;
    position: relative;
    width: 60px;
    vertical-align: top;
  }
  .trial-status-no {
    background-color: #ffe2e2;
    border-radius: 5px;
    border-style: none;
  }
  .trial-status-partial {
    background-color: #eeffee;
    border-radius: 5px;
    border-style: none;
  }
  .trial-status-unknown {
    background-color: #ffffff;
    border-radius: 5px;
    border-style: none;
  }
  .trial-num {
    text-indent: 0px;
    font-family: Arial, Helvetica, sans-serif;
    border-style: none;
    border-radius: 5px;
    bottom: 2px;
    display: inline;
    font-size: small;
    left: 3px;
    position: absolute;
    text-align: right;
  }
  .trial-result .info span {
    display: block;
    text-align: right;
    font-family: Arial, Helvetica, sans-serif;
    line-height: 10pt;
  }
  .trial-result .info {
    bottom: 2px;
    font-size: x-small;
    position: absolute;
    right: 3px;
  }
  .res-symbol {
    text-indent: 0px;
    font-family: Arial, Helvetica, sans-serif;
    line-height: 25pt;
    font-size: xx-large;
    text-align: center;
  }
  .trial-status-partial .res-symbol {
    font-family: Arial, Helvetica, sans-serif;

    font-size: x-large !important;
  }
  .trial-status-yes {
    background-color: #eeffee;
    border-radius: 5px;
    border-style: none;
  }
  .tooltip {
    display: none;
    position: absolute;
    border-radius: 5px;
    border-style: none;
    padding: 10px;
    color: #fff;
  }
  .bar rect {
    fill: #5e72e4;
  }
  .bar text {
    fill: #fff;
  }
  .axis path,
  .axis line {
    fill: none;
    stroke: #000;
  }
}
