css:
@-moz-document url-prefix("https://web.roblox.com"), url-prefix("https://www.roblox.com")
{
/* i really want this to be global */
 
 
  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  #fb-root
  {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.imgur.com/OnnQVKQ.png) !important;
      /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }



.dark-theme .section-content {
    background-color: #a51c1b;
    color: #fff;
}.dark-theme .rbx-tab .rbx-tab-heading {
    background-color: #a51c1b;
    color: #fff;
}.dark-theme .rbx-tabs-horizontal .nav-tabs {
    background-color: #a51c1b;
    color: #fff;
}.dark-theme .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    color: #fff;
    background-color: transparent;
    box-shadow: inset 0 -4px 0 0 #000;
}.dark-theme .text, .dark-theme .text-date-hint, .dark-theme .text-default, .dark-theme .text-description, .dark-theme .text-footer, .dark-theme .text-info, .dark-theme .text-label, .dark-theme .text-secondary, .dark-theme p {
    color: #ffffff;
}.dark-theme .profile-avatar-right .profile-avatar-mask {
    background-color: #a51c1b;
    color: #fff;
}.profile-avatar-left .enable-three-dee {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    border: 1px solid black;
    background-color: #ffffff00;
    border-color: #000000;
    color: #000000;
    cursor: pointer;
    display: inline-block;
    font-weight: 500;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px 9px;
    font-size: 16px;
    line-height: 100%;
    border-radius: 1px;
    border: 1px solid #000000;
    position: absolute;
    top: 12px;
    right: 12px;
    min-height: 32px;
}.dark-theme .profile-accoutrements-container .profile-accoutrements-slider .accoutrement-item a {
    background-color: #b32524;
}.dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img {
    background-color: #a51c1b;
}.dark-theme .asset-thumb-container {
    background-color: rgb(179 37 36);
    border: 0;
}.dark-theme .layer {
    background-color: #a51c1b;
}.dark-theme .slide-switcher .slide-item-container .slide-item-container-right {
    background-color: #b32524;
    color: #fff;
}.dark-theme .slide-switcher .slide-item-container .slide-item-container-left {
    background-color: #a51c1b;
}.dark-theme .carousel-control {
    background-color: #00000000;
    border-color: #ffffff6e;
}.dark-theme .border, .dark-theme .border-bottom, .dark-theme .border-left, .dark-theme .border-right, .dark-theme .border-top {
    border-color: #000000;
}.dark-theme .avatar .avatar-card-image {
    background-color: #bb0100;
}.dark-theme .rbx-left-col {
    box-shadow: 0 0 8px 0 #00000096;
    background-color: #7692af00;
}.dark-theme .rbx-header {
    background-color: #a51c1b;
    border: solid #a51c1b00;
    border-width: 0 0 1px;
    box-shadow: 0 0 8px 0 #00000096;
}.item-card-thumb-container, .item-card-thumb-container .item-card-thumb, .item-card-thumb-container div[class^=icon-], .item-card-thumb-container span[class^=icon-] {
    border-radius: 1px;
}




.avatar-thumbnail .toggle-three-dee {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #fff0;
    color: #191919;
    cursor: pointer;
    display: inline-block;
    font-weight: 500;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 9px;
    font-size: 16px;
    line-height: 100%;
    border-radius: 1px;
    border: 1px solid #000000;
    position: absolute;
    bottom: 12px;
    right: 54px;
    min-height: 32px;
}         
         .avatar-thumbnail .toggle-three-dee:focus, .avatar-thumbnail .toggle-three-dee:hover {
    background-color: #fff0;
    border-color: #ffffff;
    color: #ffffff;
    cursor: pointer;
}.dark-theme .rbx-tabs-horizontal #horizontal-tabs .rbx-tab:not(.active) a:not(:hover) {
    background-color: #a51c1b;
    color: #fff;
}.dark-theme .pill-toggle label {
    color: #ffffff;
}



.dark-theme .input-group-btn .input-dropdown-btn {
    background-color: transparent;
    border-color: #d4d7d7;
    color: hsla(0,0%,100%,.7);
    border-radius: 1px;
}.dark-theme .input-group .input-field {
    background-color: #00000000;
    border-color: #d4d7d7;
    color: #d4d7d7;
    border-radius: 1px;
}





.dark-theme .avatar-card-container {
    background-color: #b32524;
    color: #fff;
}.dark-theme .avatar-card-btns {
    background-color: #b32524;
    color: #fff;
}.dark-theme .btn-secondary-md {
    background-color: transparent;
    border-color: #ffffff;
    color: hsla(0,0%,100%,.7);
    border-radius: 1px;
}.dark-theme .avatar-card-btns .accept-friend {
    background-color: #fff6;
    border-color: #fff;
    color: #232527;
    border-radius: 1px;
}

  .avatar-back
  {
    background-image: url(https://drive.google.com/file/d/1oE53p4dnJDHjrA25SKP0j3OzxgaxYA1V/view?usp=sharing);
 }
}
