css:
@-moz-document domain("kryptoni.xyz") {
body {
		background: #f0f0f0 url(https://finobe.com/img/bgpat5.svg) no-repeat 50% fixed !important;
		-webkit-background-size: cover!important;
	}
	.navbar-dark.navbar-expand-lg {
        letter-spacing: -0.25px;
		background: #fff!important;
		border-bottom: 1px solid #e6e6e6!important;
	}
	.nav-item {
		color: black!important;
	}
	.nav-link {
		color: rgba(0,0,0,.5)!important;
	}
	.nav-link:hover {
		color: rgba(0,0,0,.7)!important;
	}
	.btn {
		text-transform: lowercase;
    }
	.navbar-light .navbar-nav .show > .nav-link {
		color: black;
	}
    .navbar-brand {
        content:url("https://finobe.com/img/BUSY.png");
        max-width: 30px;
    }
div.card-header.py-2.text-white.text-center.bg-success {
    background-color: #09f!important;
}
button.btn.btn-danger.btn-sm.btn-block {
    background-color: #a75dd7;
    border-color: #a75dd7;
}
button.btn.btn-block.btn-sm.btn-success {
    background-color: #0f7ec8;
    border-color: #0f7ec8;
}
button.btn.btn-success.join-server.btn-lg.mt-4::before {
     content: "play";
}
button.btn.btn-success.join-server.btn-lg.mt-4 {
    background-color: #00b857;
    border-color: #00b857;
}
button.btn.btn-success.join-server.btn-lg.mt-4:hover {
    background-color: #009245;
    border-color: #009245;
}
button.btn.btn-success.btn-lg.join-server {
    background-color: #00b857;
    border-color: #00b857;
}
button.btn.btn-success.btn-lg.join-server:hover {
    background-color: #009245;
    border-color: #009245;
}
i.fas.fa-gem {
    content:url("https://finobe.com/img/diu_16.png");
    max-width: 25px;
    vertical-align: middle!important;
}
}
