css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://userstyles.org") , url-prefix("https://userstyles.org") {

html {
	background: url('data:image/gif;base64,R0lGODlhAQBuAMQAAAAAAP////7+/v39/fz8/Pv7+/r6+vn5+fj4+Pf39/b29vX19fT09PPz8/Ly8vHx8fDw8O/v7+7u7u3t7ezs7Ovr6+rq6unp6ejo6Ofn5+bm5uXl5f///wAAAAAAAAAAACH5BAEAABwALAAAAAABAG4AAAU04CaKWpmdWHpdllW9FDVNkhRFEPQ8TtMwjMVCoUgkEIiD0mAoFAjQgVRADViv2Kx2y+1eQwA7') repeat-x top left !important;
	}

body {
	background: url('data:image/gif;base64,') repeat-x top left !important;
	padding: 5px !important;
	height: 48px !important;
	-moz-border-radius: 10px;
	-moz-border-radius-topleft: 0;
	-moz-border-radius-topright: 0;
        font-family: Sans-serif !important;
	font-size: 12px !important;
	margin: 0 auto 20px auto !important;
	position: relative !important;
	width: 900px !important;
	}

h1 {
	background: url('data:image/png;base64,') no-repeat top left !important;
	font-variant: normal !important;
	margin-left: 20px !important;
	margin-top: 0px !important;
	margin-bottom: 25px !important;
	padding-top: 58px !important;
	padding-left: 0px !important;
	}

p {
	padding: 0 30px 0 20px !important;
	text-align: justify !important;
	}

#install p {
	text-align: left !important
	}

#other-links, #other-links a {
	color: #FFF !important;
	font-size: small !important;
	}

#other-links {
	padding-top: 6px !important;
	padding-right: 20px !important;
	}

#breadcrumbs, #breadcrumbs a {
	margin-left: 0px !important;
	color: #FFF !important;
	font-size: 11px !important;
	}

a {
	text-decoration: none !important;
	}

a:hover {
	text-decoration: underline !important;
	}

#style-meta {
	margin-left: 20px !important;
	margin-top: -24px !important;
	}

#sidebar {
	margin-top: -53px !important;
	}

input {
	font-size: small !important;
	}

#install {
	border: none !important;
	background: url('data:image/png;base64,') bottom repeat-x;
	color: white !important;
	padding: 4px !important;
	font-size: 11px !important;
	}

#install a {
	color: #c5f1a7 !important;
}

#install > h2 {
	font-size: 0 !important;
	margin: auto !important;
	background: url('data:image/png;base64,') no-repeat !important;
	width: 138px !important;
	height: 30px !important;
	margin-top: 0px !important;
	}

#show-code, #screenshots {
	clear: right !important;
	}

#obsoletion-message {
	border: none !important;
	width: 38% !important;
	}

#generic-obsoletion-message {
	text-align: center !important;
	}

.ad {
	display: none !important;
	}

#search-terms {
	margin-left: 1em;
	-moz-appearance:none !important;
	padding-left: 2px !important;
	padding-right: 2px !important;
	background-color: #DDD !important;
	border: 1px solid #7E94A6 !important;
	width: 15em !important;
	font-family: Sans-serif !important;
	-moz-border-radius: 5px !important;
	}

#search-terms:hover {
	background-color: #EEE !important;
	}

#search-terms:focus {
	background-color: #FFF !important;
	}

#search-submit {
	-moz-appearance: none !important;
	background: #7392C1 !important;
	color: white !important;
	border: 1px solid #4444AA !important;
	font-weight: bold !important;
	-moz-border-radius: 5px !important;
	font-family: Sans-serif !important;
	}

#search-submit:hover {
	background: #83A2D1 !important;
}

#style-list {
	margin-top: 0 !important;
	margin-bottom: 0 !important;
	}

.autoPagerS {
	display: none !important;
	}

.autopagerize_page_separator, .autopagerize_page_info {
	display: none;
	}

}

@-moz-document url-prefix("http://userstyles.org/style_screenshots/") , url-prefix("https://userstyles.org/style_screenshots/") {

html {
	background: none !important;
	}

body {
	background: none !important;
	position: static !important;
	width: auto !important;
	padding: 0 !important;
	}

}
