css:
@-moz-document domain("ezcha.net") {
.ezheader[data-v-3ce40631], .portable[data-v-3ce40631] {
    width: 100%;
    background: linear-gradient(90deg,#001D7C,#001869);
}
.spill[data-v-3ce40631] {
    background: linear-gradient(90deg,#F70000,#C10000);
}
.grad-text {
    background: linear-gradient(0deg,#9e9e9e,#fff);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
.ezcontent[data-v-1ee9f435] {
    background: #282c3f;
}
.fill[data-v-706c2da6], .split {
    background: linear-gradient(90deg,#fff,#9e9e9e);
}
.eznav .router-link-active {
    background: linear-gradient(0deg,#9e9e9e,#fff);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
     -webkit-animation: glow 1s ease-in-out infinite alternate;
  -moz-animation: glow 1s ease-in-out infinite alternate;
  animation: glow 1s ease-in-out infinite alternate;
}
input[type=email], input[type=number], input[type=password], input[type=text], textarea {
    background-color: #373c52;
    border: 1px solid #404660;
    border-bottom: 2px solid #fff;
    outline: none;
}
.activator[data-v-02c49fc2], .activator[data-v-291eeb1b]  {
    background-color: #373c52;
    border: 1px solid #404660;
    background-image: url(https://i.imgur.com/fDPunJC.png);
    background-origin: content-box;
    background-repeat: no-repeat;
    background-position: 100%;
}
.btn a[data-v-05dec15e] {
    color: #fff;
    text-transform: uppercase;
    border-radius: 4px;
    font-weight: 700;
    padding: 6px 12px;
    box-shadow: 0 3px 4px rgba(0,0,0,.15);
    transition: background-color .2s linear;
    background-color: #F70000;
}
.activator.expanded[data-v-02c49fc2], .activator.expanded[data-v-291eeb1b] {
    background-color: #373c52;
    border: 1px solid #404660;
    background-image: url(https://i.imgur.com/c1gmxOT.png);
}
.option[data-v-02c49fc2], .option[data-v-291eeb1b] {
    background-color: #373c52;
}
.dropdown[data-v-02c49fc2] {
    background-color: #373c52;
    border: 1px solid #404660;
}
input[type=email]:hover, input[type=number]:hover, input[type=password]:hover, input[type=text]:hover, textarea:hover {
    background-color: #4d536c;
    border: 1px solid #5e6583;
    border-bottom: 2px solid #fff;
}
.activator[data-v-02c49fc2]:hover, .activator[data-v-291eeb1b]:hover {
    background-color: #4d536c;
    border: 1px solid #5e6583;
}
input[type=email]:focus, input[type=password]:focus, input[type=text]:focus, textarea:focus {
    background-color: #4d536c;
    border: 1px solid #5e6583;
    border-bottom: 2px solid #9d9d9d;
}
.card {
    background-color: #4d536c;
}
a {
    transition: color .2s linear;
    text-decoration: none;
    color: #d0d4ff;
}
a:hover {
    color: #fff;
}
.btn:hover a[data-v-05dec15e] {
    background-color: #b00707;
}
div>svg[data-v-dc607230] {
    width:0%;
    height:0%;
}
.logo-container[data-v-3ce40631] {
    content:url("https://i.imgur.com/lVKATKN.png"); 
    padding-top: 0px;
    width: 150px;
    height: 150px;
}
.mini>.logo-container[data-v-3ce40631] {
    content:url("https://i.imgur.com/lVKATKN.png"); 
    padding-top: 0px;
    width: 100px;
    height: 100px;
}
.award>img[data-v-b776115c] {
    content:url("https://i.imgur.com/utmIwiG.png"); 
}

@-webkit-keyframes glow {
  from {
    text-shadow: 0 0 1px #fff, 0 0 7px #c1c1c1;
  }
  to {
    text-shadow: 0 0 1px #fff, 0 0 3px #f0f0f0, 0 0 4px #bbb, 0 0 5px #aaa, 0 0 6px #b2b2b2, 0 0 7px #cacaca, 0 0 8px #b7b7b7;
  }
}

}
