css:
@-moz-document domain("wellgoodnight.com") {
div.bbWrapper{
    font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    padding: 0px;
    margin-top: 12px;
    
}



div.p-header-logo.p-header-logo--image{
    
    height: 0%;
    width:  0%;
}

.p-body-inner {
    
    width: 100%;
    max-width: 100%;
}

.p-nav-inner {
    
    width: 100%;
    max-width: 100%;
}

.p-sectionLinks-inner {
    width: 100%;
    max-width: 100%;
}

#header{
    background-image: url("https://wellgoodnight.com/index.php?attachments/rogan_header-scat-gif.825/");
    background-repeat: no-repeat;
    height: 90px;
    width: 100%;
    font: 13px Helvetica,Arial,Verdana,sans-serif;
}

#top{
    
    font: 13px Helvetica,Arial,Verdana,sans-serif;
}

div.message-cell{
    
    background: none;
    background-color: #111;
    
    font: 12px sans-serif;
    
    font-family: sans-serif;
    font: 10px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    
    
}

div.message-cell.message-cell--user{
    
    background: none;
    background-color: #111;
    
    
    font: 12pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    
    
}

.message-attribution{
    font: 122pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

div.bbCodeBlock-content{
    background-color: #202020;
}

.p-header-logo{
    color: white;
}

a.actionBar-action.actionBar-action--report {
    font: 0px sans-serif;
}

div.reactionsBar.js-reactionsList.is-active{
    font: 10px sans-serif;
    color: white;
    background-color: #202020;
    margin-bottom: 8px;
}


aside.message-signature
{
    font: 100px white
}




header.message-attribution.message-attribution--split{
    font-size: 10px;
}

a.actionBar-action{
    
    margin-top: 30px;
    color: white;
    font-size: 8px;
    
}

div.message-responseRow.u-jsOnly.js-commentLoader.p-description.uw-comment-count{
    font-size: 12px;
    background-color: #202020;
    
}


div.message-responseRow{
    
    
    background-color: #202020;
}

.bbCodeBlock{
    border-left: 3px solid #9A8F7D;
}

a.actionBar-action.actionBar-action{
    font-size: 12px;
    margin-top: 2px;
    
}

div.input{
    background-color: #202020;
}

a.actionBar-action.actionBar-action--report
{
    font-size: 0px;
}

footer.message-footer{
    margin-top: 2px;
}

div.comment-content div.comment-contentWrapper{
    font-size: 16px;
}

div.comment-actionBar.actionBar.fcs_comment_date{
    font-size: 12px;
}

footer.comment-footer div.sd_reactions.comment-reactions.js-commentReactionsList.is-active{
    font: 10px sans-serif;
}

div.structItemContainer-group.structItemContainer-group--sticky{
    background-color: #161616;
}

div.structItemContainer-group.js-threadList{
    background-color: #0b0b0b;
    
    
}



div.fr-box.bbWrapper.fr-ltr.fr-basic.fr-top{
    background-color: #121212;
    
}





.node--unread .node-icon i::before{
    color: white;
}

.p-sectionLinks{
    color: #9A8F7D;
}

.pageNav-page{
    color: #9A8F7D;
}

a.pageNav-jump.pageNav-jump--prev{
    color: #9A8F7D;
}

a.pageNav-jump.pageNav-jump--next{
    color: #9A8F7D;
}

li.pageNav-page.pageNav-page--current{
    background-color: #B8966D;
    border: 1px solid #B8966D;
}

a.button--cta.button.button--icon.button--icon--write{
    background-color: #9A8F7D;
    border: 1px solid #9A8F7D;
}

.message.message--post{
    margin-top: 14px !important;
}

.block-textHeader{
    color: #9A8F7D;
}

.block-header{
    color: #dedede
}

.block-header .block-desc{
    color: #dedede
}

.message-newIndicator, a.message-newIndicator{
    background-color: #9A8F7D;
}

.username--style2{
    color: #9A8F7D;
}

a{
    
   color: #9A8F7D;   
    
}



a:visited
{
    
    
    color: grey;
}


div.structItem-title
{
    
    font: 14pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}


.discussionListItem.unread
{
    color: orange !important;
}

.is-unread .structItem-title a {
    color: #9A8F7D;
}

.node--unread .node-title a {
    color: #9A8F7D;
}




}
