css:
@-moz-document domain("bilibili.com") {
body {
/*     background-image:url(https://w.wallhaven.cc/full/dg/wallhaven-dgq5rl.jpg); */
    background-color:rgb(239, 239, 244);
    
    background-size: 100%;
    background-repeat:no-repeat;
    background-attachment: fixed;
    transition: background-color .5s;
}
/* 顶栏背景 */
.international-header .mini-type, .van-popover .mini-type{
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 顶栏广告或者是叫推广 */
.banner-link[data-v-7d26eca9]{
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    transition: all 0.4s ease 0s;
}
/* 顶栏广告或者是叫推广 消失过度动画 */
.bili-banner .banner-link[data-v-7d26eca9]:hover{
    background-color: rgba(255, 255, 255, 0)!important;
    backdrop-filter: blur(0) saturate(180%);
}
/* 顶栏主站抽屉美化 */
.channel-menu-mini{
    border-radius:10px;
    margin-top:10px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 顶部间距 */
.v-wrap{
    margin-top:20px;
}

/* UP主信息宽度 */
.v-wrap .r-con{
    width:336px;
}
/* UP主信息Padding */
.v-wrap .up-info{
    padding-left:9px;
}

/* UP主信息 */
.v-wrap .up-info{
    border-radius:5px 5px 0px 0px;
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}

.v-wrap .danmaku-wrap{
    
    background: rgb(255 255 255 / 0%);
}
/* 弹幕列表头部透明 */
.bui-collapse .bui-collapse-header, .bui-collapse .bui-collapse-header { 
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border-radius:0px;
}
/* 弹幕列表头部宽度填充 */
.bui-collapse .bui-collapse-header{
    width:105%;
}
/* 弹幕列表右透明 */
.v-wrap .danmaku-wrap{ 
    background: rgb(255 255 255 / 0%);
}
/* 弹幕列表左透明 */
.player-auxiliary-area .player-auxiliary-filter{
    
    background: rgb(255 255 255 / 0%);
}
/* 弹幕列表宽度填充 */
.bui-collapse .bui-collapse-body{
    width:105%;
}
/* 弹幕列表背景 */
.player-auxiliary-area .player-auxiliary-collapse .bui-collapse-body{

    background-color:#fff0;
}
/* 弹幕列表头部背景等级1 */
.player-auxiliary-area .player-auxiliary-danmaku .player-auxiliary-danmaku-function{
    background-color: #fff0;
}
/* 弹幕列表头部背景等级2 */
.player-auxiliary-area .player-auxiliary-danmaku .player-auxiliary-danmaku-function [class*=player-auxiliary-danmaku-btn-]{
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 弹幕列表内容背景 */
.player-auxiliary-danmaku-wrap{
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 发送时间右侧填充 */
.player-auxiliary-area .player-auxiliary-danmaku .player-auxiliary-danmaku-function [class*=player-auxiliary-danmaku-btn-].player-auxiliary-danmaku-btn-date, .player-auxiliary-area .player-auxiliary-danmaku .player-auxiliary-danmaku-function [class*=player-auxiliary-danmaku-btn-].player-auxiliary-danmaku-btn-uname{
    width:106px;
}
/* 视频选集（P） 背景*/
.multi-page{
    border-radius:5px;
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 收纳箭头缩进 待更新 */



/* 查看历史弹幕背景1 */
.danmaku-wrap .player-auxiliary-area .player-auxiliary-danmaku-btn-footer{
    border-radius:0px 0px 5px 5px;
    background-color: rgba(255, 255, 255, .75)!important;
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 查看历史弹幕背景2 */
.bui-button.bui-button-gray2{
    background-color: #f4f4f400;
}
/* 接下来播放 */
.recommend-list{
    border-radius:5px;
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(20px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    padding:9px;
}
/* 接下来播放  视频标题 */
.video-info {
    border-radius:5px 5px 0px 0px;
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(10px) saturate(180%); 
     
}
.video-title {
    margin-left:8px;
}
.video-data{
    margin-left:8px;
}

/* 右侧栏活动推广屏蔽 */
.v-wrap #right-bottom-banner{
    display:none;
}
/* 右侧栏直播推广屏蔽 */
.v-wrap #live_recommand_report{
    display:none;
}
/* 视频左右测发光隐藏 */
.bilibili-player{
    -webkit-box-shadow: 0 0; 
    box-shadow: 0 0;
}

/* 弹幕功能条 */
.bilibili-player-video-danmaku-root{
    background-color: rgba(255, 255, 255, 0)!important;
    
}
/* 弹幕输入背景 */
.bilibili-player-video-sendbar .bilibili-player-video-inputbar{
    background: #f4f4f400;
}
/* 输入框边框 */
.bilibili-player-video-sendbar .bilibili-player-video-inputbar .bilibili-player-video-inputbar-wrap{
    border:0;
}
/* 发送按钮背景 */
.bilibili-player .bui-button.bui-button-blue, .bilibili-player .bui-button.bui-button-gray3:hover, .bilibili-player .bui-button.bui-button-gray:hover{
    border-radius: 8px;
    background-color: rgb(99 198 229);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border: 0;
}

/* 弹幕发送字体颜色 */
.bui-button.bui-button-blue, .bui-button.bui-button-gray3:hover{
    color:#fff;    
}

/* 点赞 */
.video-toolbar .ops{
    margin-left:8px;
    margin-top:8px;
}
/* 稿件投诉 */
.rigth-btn{
    margin-left:8px;
    margin-top:8px;
}
/* 更多 */
.more{
    margin-left:8px;
    margin-top:8px;
}
/* 未经作者授权，禁止转载 */
.copyright{
    margin-left:8px;
    margin-top:8px;
}
/* UP介绍 */
.info{
    margin-left:8px; 
}
/* 展开更多 */
.btn{
    margin-left:8px; 
}
/* 标签美化 */
.s_tag .tag-area>li{
    background-color: rgba(255, 255, 255, .75);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border:0;
}
/* 加号标签美化 */
.s_tag .btn-add{
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    background-color: rgba(255, 255, 255, .75);
}
/* 加号横位置修正 */
.s_tag .btn-add .one{
    left: 5.5px;
    top: 11px;
    width: 12px;
    height: 2px;
}
/* 加号竖位置修正 */
.s_tag .btn-add .two{
    left: 10.5px;
    top: 6px;
    width: 2px;
    height: 12px;
}

/* 隐藏推广 */
.activity-m{
    display:none;
}
/* 分割线隐藏 */
.v-wrap .s_tag{
    border-bottom:0;
}
/* 评论Padding */
.v-wrap #comment{
    padding: 9px;
}
/* 评论框 */
.bb-comment .comment-send .textarea-container textarea, .comment-bilibili-fold .comment-send .textarea-container textarea{
    background-color: rgba(255, 255, 255, .75);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border: 0;
}
/* 评论框 白条有待优化 鼠标焦点*/
.core-style[data-v-23e5f326]:hover {
    background-color: rgba(255, 255, 255, .49);
}
/* 发表评论 */
.bb-comment .comment-send .comment-submit, .comment-bilibili-fold .comment-send .comment-submit{
    border-radius: 8px;
    background-color: rgb(99 198 229);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border: 0;
}
.bb-comment .comment-send .comment-emoji, .comment-bilibili-fold .comment-send .comment-emoji{
    color:#757575;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 评论分割线 隐藏 */
.bb-comment .comment-header, .comment-bilibili-fold .comment-header{
    border-bottom: 0;
}
/* 评论分割线2 隐藏 */
.bb-comment .comment-list .list-item .con, .comment-bilibili-fold .comment-list .list-item .con{
    border-top: 0;
}
/* 点赞投币收藏 */
.v-wrap .video-toolbar{
    border-radius:0px 0px 5px 5px;
    background-color: rgba(255, 255, 255, .75)!important;
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%); 
    margin-top:0px;
    backdrop-filter: blur(10px) saturate(180%); 
}
.video-desc{
    padding: 8px;
    border-radius: 5px 5px 5px 5px;
    background-color: rgba(255, 255, 255, .75)!important;
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    backdrop-filter: blur(10px) saturate(180%); 
}
.comment-m{
    border-radius:5px 5px 5px 5px;
    background-color: rgba(255, 255, 255, .75)!important;
    backdrop-filter: blur(10px) saturate(180%); 
}
.bb-comment {
    background-color:rgba(255, 255, 255, .0);
}

/* 搜索页面 搜索框 有bug待修复 done*/
.head-contain{
    position: sticky;
    z-index: 999;
    border-radius: 10px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%); 
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    padding: 20px;
    margin-top: 20px;
    margin-bottom: 20px;
}
/* 搜索页面 搜索列表 done*/
.suggest-wrap{
    border: 0;
    border-radius: 10px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 搜索页面 分割线 后期可优化这 */
#navigator .v-switcher-header-item{
    border-bottom: 0;
}

/* 视频背景 间距 */
.video-list .video-item.matrix{
    float: left;
    margin-right: 34px;
    margin-top: 20px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%); 
}
/* 视频观看次数 创建时间 */
.so-icon{
    color: #000000ad;
}
/* 视频作者 */
.video-item .so-icon>a{
    color: #000000ad;
}

/* 视频背景2 */
.video-item.matrix{
    position: relative;
    width:168px;
    height: 100%;
    border-radius: 8px;
    border: 0;
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 视频创建日期宽度修复 */
.video-item.matrix .info .time{
    width:100px;
}
/*视频搜索结果 竖式*/
.video-item.list{
    padding: 10px 0 25px 172px;
    border-bottom: 0;
    margin-top: 20px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%); 
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/*视频搜索结果是影视or番剧...*/
.bangumi-item{
    border-bottom: 0; 
    padding: 20px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    border-radius: 10px;
    height:auto;
    min-height: 214px;
    margin-top: 20px;
}
/*视频搜索结果是影视or番剧...的集数对齐 修复*/
#pgc-navigate-wrap .ep-box.grid .ep-sub:nth-child(13n+1){
    margin-left:6px;
}
/*视频搜索结果是影视or番剧...的集数按钮样式*/
#pgc-navigate-wrap .ep-box.grid .ep-sub{
    border-radius: 8px;
    background-color: rgb(255 255 255);
    border: 0;
}
/*视频搜索结果是影视or番剧...的集数按钮边框*/
#pgc-navigate-wrap .ep-box .ep-sub .ep-item{
    border:0;
}
/*视频搜索结果是UP主 待完善*/
#all-list .mixin-list ul:not(:last-child){
    border-bottom: 0;
    padding: 20px;
    margin-top: 20px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 视频类别 */
.filter-wrap{
    padding: 20px 0px 20px 0px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%); 
}
/* 类别大类 */
.filter-wrap .filter-item.active{
    margin-left: 20px;
    background-color: rgba(255, 255, 255, .75);
}
/* 类别大类字体颜色 */
.filter-wrap .filter-item.active a{
    color:#00a1d6;
}
/* 类别大类 全部分区 */
.filter-wrap .filter-type.tids_1>.filter-item{
    margin-right:9px;
}
.filter-wrap .filter-type.tids_1>.filter-item:nth-of-type(19){
    margin-top:10px;
    margin-left:100px;
}
.filter-wrap .filter-type.tids_1>.filter-item:nth-of-type(20){
    margin-top:10px;
}

/* 视频排序方式图标位置 */
.switch-wrap .aver{
    right:50px;
}
.switch-wrap .imgleft{
    right:20px;
}
/* 分割线 */
.filter-wrap .filter-wrap_bottom{
    background:0;
}
/* 搜索框 */
.search-wrap .search-block .input-wrap{
    background:#fff0;
}
/* 视频界面顶部搜索框 */
.international-header .mini-type .nav-search #nav_searchform, .van-popover .mini-type .nav-search #nav_searchform{
    background-color: rgba(255, 255, 255, .75);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border: 0;
}
/* 视频界面顶部搜索框搜索按钮背景 */
.bili-header-m .nav-search .nav-search-btn, .international-header .nav-search .nav-search-btn{
    height:auto;
    background:#00a1d696;
}

/* 搜索页面 页码 */
.page-wrap .pager{
    border-radius: 10px;
    margin-top: 20px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%); 
}

/* 主站抽屉里面的分割线 */
.channel-menu-mini .r-box{
    border-left: 1px solid #ccc;
}

/*分界线 频道样式修改*/
.international-header .primary-menu-itnl{
    margin-top: 20px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
    border-bottom: 0;
}
/*首页 动态...边框修改*/
.page-tab .con li{
    border: 0;
}
/* 标签 */
.sub-nav-m ul li a{
    font-size: 18px;
    font-weight: 700;
}
/* 标签 */
.sub-nav-m{
    backdrop-filter: blur(10px) saturate(180%);
    padding: 10px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .9);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 播放量与弹幕数 */
.spread-module{
   height:150px 
}
/* 播放量与弹幕数 */
.spread-module .num{
    background-color: rgb(255, 255, 255);
    backdrop-filter: blur(10px) saturate(180%);
}
/* 视频 */
.spread-module{
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 播放量与弹幕数 */
.bili-wrapper .r-con{
    padding: 10px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
.bili-wrapper{
    width:1250px;
}
/* 轮播图右侧热门 */
.groom-box-m .groom-module{
    margin: 0 0 20px 40px;
}
/* 热门轮播块 */
.nominate-m{
    padding: 10px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .9);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 前方高能 */
div#high_energy{
    padding: 10px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .9);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 影视杂谈 影视剪辑...分类块 */
.sub-zone-m{
    padding: 10px;
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .9);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}

/* 分割线 动态首页背景 */
.fixed-bg[data-v-1f01eefc] {
    background-image:url(https://w.wallhaven.cc/full/9m/wallhaven-9m5321.jpg);
    background-size: 110% 100%;
    animation: gradientBG 15s ease infinite;
}
@keyframes gradientBG {
	0% {
		background-position: 0% 50%;
	}
	50% {
		background-position: 100% 50%;
	}
	100% {
		background-position: 0% 50%;
	}
}

/* 我关注的UP主的动态界面 */
.user-panel[data-v-f8464120]{
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 正在直播 */
.live-panel[data-v-420e12aa]{
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 正在直播 右侧更多修复*/
.live-panel .more-button[data-v-420e12aa]{
    width:40px;
}
/* 动态输入框背景1 */
.home-page .home-container .home-content .center-panel .section-block[data-v-1f01eefc]{
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 动态输入框背景2 */
.core-style[data-v-23e5f326]{
    background-color: #ffffff00;
}
/* 动态输入框下方按钮背景3 */
.publish-panel[data-v-28ce1694]{
    background-color: #ffffff00;
}

/* 全部动态 */
.most-viewed-panel[data-v-28eb68ea]{
    border-radius: 8px;
    background-color: rgba(255, 255, 255, .75);
    backdrop-filter: blur(10px) saturate(180%);
    box-shadow: 0 10px 20px rgb(0 0 0 / 5%), 0 5px 10px rgb(0 0 0 / 3%);
}
/* 动态分类 */
