css:
@-moz-document domain("uvirtual.usach.cl") {
/*version 0.210318*/

body {
    background-color: #1c1c1c;
    color: #b5b3b2;
}
h3{
	color: #5ebdf7;
}


.card, .userprofile .profile_tree section {  /* cuadros de texto flotantes */
    background-color: #515151;
}



#region-main {
    /* cuadros contenedores flotantes */
    background-color: #404040;
}
#page .course-content ul li.section.main {
    /* cuadros de texto */
    background-color: #404040 !important;
    color: #f5aa1d;
}
.instancename {
    color:#ea7600;
}
#header .header-menubar {
    background: #a45300;
}
#frontpage-course-list h2 {
    background: #1c1c1c;
}

.header_menu_link{
    background: rgba(0,0,0,0) !important;
}
[data-region="drawer"] {
    background-color: #1c1c1c;
}
.list-group-item {
    /* botones inactivos del menu lateral */
    background-color: #404040;
    color:#ea7600;
}
.list-group-item.active {
    /*boton activo del menu lateral*/
    border-color:#ea7600;
    background-color:#262626;
    color: #5ebdf7; /*texto celeste de boton activo*/
}
.list-group-item-action:hover, .list-group-item-action:focus {
    color: #f5aa1d;
    background-color: #262626;
}
.discussionname {
	color: #5ebdf7;
}
.activity-navigation a.btn.btn-link{
	color: #ea7600;
}
.forumpost .options .commands > a {
	color: #ea7600;
}

.region_main_settings_menu_proxy {
 background-color: rgba(0,0,0,0);
}

div#frontpage-course-list.mycourses-course-list h2 {
    color: #2d9cdb;
}
.bg-white, .bg-light {
 background-color: #1c1c1c !important;
}

.moodle-dialogue-base .moodle-dialogue-wrap.moodle-dialogue-content {
 background-color:#404040;
}

legend {
 color:#ff8e27;
}

.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
 background-color:#515151;   
 color:#2aa7ee;
}
.bg-secondary {
 	background-color: #404040 !important;

}

.generaltable tbody>tr:nth-child(odd)>td, .forumheaderlist tbody > tr:nth-child(odd) > td
{
 background-color:#404040 !important;
}
.generaltable tbody>tr:nth-child(odd)>th, .forumheaderlist tbody > tr:nth-child(odd) > td
{
 background-color:#404040 !important;
}

.generaltable thead th, .forumheaderlist tbody > tr > td {
    background: #515151 !important;
    color: #c9c9c9 !important;
}


/***********************************/
/******* EXPERIMENTAL!!! ***********/
/***********************************/
/*
	color: #2d9cdb;    	color celeste
	color: #185a8e;		color celeste oscuro

	color: #ff7500;		color naranjo oscuro
	color: #ff8e27;		color naranjo claro
	
	color: #c9c9c9;		color gris claro 1
	color: #a7a7a7;		color gris claro 1
	color: #838383;		color gris claro 2
	color: #515151;		color gris claro 3
	color: #404040;		color gris medio
	color: #1c1c1c;		color gris oscuro
*/


.generaltable tbody tr:hover>td, .forumheaderlist tbody > tr:hover > td,
.generaltable tbody tr:hover > td > a, .forumheaderlist tbody > tr:hover > td > a:hover
{
	background-color:#515151 !important;
	color: #ffae65 !important;
    border-color: #ff8e27;
              
}
.generaltable tbody tr:hover>th, .forumheaderlist tbody > tr:hover > td,
.generaltable tbody tr > th > a:hover, .forumheaderlist tbody > tr > td > a:hover
{
 	background-color:#515151 !important;
    color: #ffae65 !important;
    border-color: #ff8e27;
}

.alert-info,
#page-admin-index .releasenoteslink,
#adminsettings .form-overridden,
.que .formulation {
 	background-color:#404040;
    color: #ff7500 ;
}
}
