@namespace url(http://www.w3.org/1999/xhtml);

Credits[Strecher] nnm.ru {}

@-moz-document domain("nnm.ru") {
div[id="top-banner"] { display: none !important; }

div[id="rightMenu"] { display: none !important; }

div[id="bottom-banner"] { display: none !important; }

div[id="topmenu"] { display: none !important; }

div[id="footer"] { display: none !important; }

div[id="form-0"] { display: none !important; }

div[class="left-banner"] { display: none !important; }

div[class="center-banner"] { display: none !important; }

div[class="avatar"] { display: none !important; }

div[class="cut"] img { display: none !important; }

div[id="body"]{width: 100% !important;margin: 0px 0 0 0 !important; }

div[id="leftMenu"] { position:fixed !important; top:0 !important; right:0 !important; width:150px !important; height:97% !important; overflow:auto !important; padding:10px !important;margin:0 !important;background-color:#f7f7f7 !important; margin:0px !important; border:1px solid #aaa !important; text-align:justify !important; }

div[id="page"] { float:none !important; background-color:#fff !important; border:0px !important; padding: 0 175px 0 0 !important; margin: 0px !important; width: auto !important; }

div[id="content"] { border:0px !important; width:100% !important; padding: 0px !important; margin: 0px !important; }

div[id="new"],
div[class="content"]  { margin:0 !important; padding:0 !important;width:100% !important;float:none !important;clear:both!important;}

div[class="docinfo"] { background-color:#fff !important; border:0 !important; margin:0 !important; padding:10px !important;width:100% !important; }

div[class="opis"]{}

div[id="komments"] { margin:0 !important; padding:0 !important;width:100% !important;border:0 !important;float:none !important;clear:both!important;}

div[class="new"] { margin:0 !important; padding:0 !important;width:98% !important;float:none !important;clear:both !important;}

div[class="new-text"] { font-family: Collibri !important; font-size: 175% !important; background-color:#f7f7f7 !important; border: 1px dotted #0081ce !important; padding:20px !important; margin: 10px auto 0 auto !important; width:98% !important;float:none !important;clear:both !important;}

div[class="new-text"] b { font-family: Collibri !important; font-size:100% !important; color:#0081ce !important; width:100% !important;}

div[class="toolbar"] { background-color:#333 !important; margin:0 !important; border:0 !important; width:100% !important;}

div[class="listing"] { background-color:#fff !important; float:none !important;clear:both !important;margin:0 auto !important; padding:5px 10px !important; border:0 !important; }

ul[class="new-menu"] {border-top:3px solid #e5e5e5; border-bottom:1px solid #fff;background:#f5f5f5 !important;margin:0 !important;padding:10px 10px 20px 10px !important; color:#bbb !important; width:100% !important; float:none !important; clear:both !important; display:block !important; }

div[id="new-reyt"] {border-top:3px solid #e5e5e5; border-bottom:1px solid #fff;background:#f5f5f5 !important;margin:0 !important;padding:0 !important; color:#bbb !important; width:100% !important; float:none !important; clear:both !important; display:block !important; }

div[id="golosovalka"]{border:0px !important;background:#fff !important;margin:0 0 20px 0 !important;padding:0 !important; width:100% !important; float:none !important; clear:both !important; display:block !important;}

div[id="golosovalka"] div {border-top:3px solid #e5e5e5 !important; border-bottom:1px solid #e5e5e5 !important;background:#f5f5f5 !important;margin:0 auto !important;padding:0 !important; width:100% !important; float:none !important; clear:both !important; display: block !important; }

div[id="golosovalka"] table{ width:98% !important;border:0 !important}

ul [class="new-admin"] { width: 200px !important; border:1px dotted #ddd !important; }

div[class="tags"] { border-top:5px solid #e5e5e5; border-bottom:1px solid #fff;background:#f5f5f5 !important;margin:0 !important;padding:5px 10px !important; color:#bbb !important; width:100% !important; float:none !important; clear:both !important;}

div[class="tags"] a { color:#000 !important; text-decoration:none !important; }

img[class="new-image-center"] { margin-top:8px !important; border:1px solid #eee !important; padding:0px !important; }

h1[class="new-title"],
h2[class="new-title"] a,
h2[class="new-title"] { font-family: Collibri !important; font-size: 24px !important; font-weight: normal !important; padding: 10px 20px 10px 20px !important; background: #0081ce !important; color: #fff !important; width:100% !important}

div[class="komment"],
div[class="info"], div[class="moder"] { margin:2px 0 !important; padding:10px 0 !important; border:1px dotted #0081ce !important;width:100% !important; float:none !important; clear:both !important; }
div[class="komment"] {font-size: 150% !important; background-color:#f7f7f7 !important; padding:10px 10px 30px 10px !important; float:none !important; clear:both !important; }
div[class="komment"] ul{margin:0px 10px 0 0 !important; }
div[class="komment"] ul a {font-size: 110% !important; padding:10px 0 !important; }
div[class="komment"] span { padding-right:2000px !important; padding-bottom:10px !important; }

div[class="level-2"] span,
div[class="level-3"] span,
div[class="level-4"] span,
div[class="level-5"] span,
div[class="level-6"] span,
div[class="level-7"] span,
div[class="level-8"] span,
div[class="level-9"] span,
div[class="level-10"] span { padding-right:2000px !important; padding-bottom:10px !important; }

div[class="level-2"],
div[class="level-3"],
div[class="level-4"],
div[class="level-5"],
div[class="level-6"],
div[class="level-7"],
div[class="level-8"],
div[class="level-9"],
div[class="level-10"] {font-size:100% !important; background-color:#e5e5e5 !important; border:1px dotted #aaa !important; margin:0 0 -20px 20px !important; padding:10px !important; float:none !important; clear:both !important; width:85% !important; }

div[class="level-2"] i,
div[class="level-3"] i,
div[class="level-4"] i,
div[class="level-5"] i,
div[class="level-6"] i,
div[class="level-7"] i,
div[class="level-8"] i,
div[class="level-9"] i,
div[class="level-10"] i{font-size:100% !important; }

div[class="level-2"] ul,
div[class="level-3"] ul,
div[class="level-4"] ul,
div[class="level-5"] ul,
div[class="level-6"] ul,
div[class="level-7"] ul,
div[class="level-8"] ul,
div[class="level-9"] ul,
div[class="level-10"] ul{margin-bottom:-10px !important;}

div[class="level-3"] {margin-left:40px !important;}
div[class="level-4"] {margin-left:60px !important;}
div[class="level-5"] {margin-left:80px !important;}
div[class="level-6"] {margin-left:100px !important;}
div[class="level-7"] {margin-left:120px !important;}
div[class="level-8"] {margin-left:140px !important;}
div[class="level-9"] {margin-left:160px !important;}
div[class="level-10"] {margin-left:180px !important;}

div[class="tagdoc"] {border:1px solid #ff6900 !important; background:#FFEADB !important; }

li[class="auth"] { display:none !important; }

div[class="zakladki"] { background-color:#fff !important; border-bottom:2px solid #333 !important; width:100% !important;}

div[class="zakladki"] ul { background-color:#ffffdd !important; border-bottom:0 !important; width:100% !important;}

textarea { border:1px dashed #aaa !important; padding:5px !important; }

body{ background:none !important;}

}
