css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {
  #dashboard_index {
    background: none #000000 !important;
    color: #342a1c !important;
  }
  #dashboard_index, #dashboard_index div.l-header, #dashboard_index #container, #dashboard_index div.l-content, #dashboard_index #dashboard_edit_post {
    background: none #000000 !important;
  }
  #dashboard_index .dashboard_nav_item {
    background: none #000000 !important;
}
  #dashboard_index div.l-header {
    position: absolute !important;
    top: 0px !important;
    left: 0px !important; 
    height: 250px !important;
    width: 100% !important;
    background: #342A1C url('http://img407.imageshack.us/img407/9803/sonnetbycugar.jpg') center !important;
  }
  #dashboard_index #new_post_notice_container {
    position: absolute !important;
    top: 60px !important; 
    left: 30px !important;
  }
  #dashboard_index #logo {
    top: 100px !important;
    left: 50px !important;
  }
  #dashboard_index #nav {
    top: 120px !important;
    right: 50px !important;
  }
  #dashboard_index #search_query, #dashboard_index .search_icon, #dashboard_index #tip{
    display: none !important;
  }

  #dashboard_index #dashboard_controls a, #dashboard_controls .control {
    color: #342a1c !important;
  }
  #dashboard_index #content_top, #dashboard_index #content_bottom {
    display: none !important;
  }
  #dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    margin-top: 280px !important;
  }
  #dashboard_index .dashboard_nav_item.active {
    background: none #ffffff !important;
    color: #000000 !important;
    border: 2px solid #FFFFFF !important; 
  }
  #dashboard_index .dashboard_nav_item a:hover, #dashboard_index .tumblelog_link:hover {
    color: #999999 !important;
  }
  #dashboard_index .nav_item {
    color: #000000 !important;
  }
  #dashboard_index .arrow {display:none !important;}
  #dashboard_index .nav_item.active {
    color: #ffffff !important;
    background: none #000000 !important
  }
  #dashboard_index .post {
    border: none !important;
    border-bottom: 1px solid #C2C2C2 !important;
    color: #342a1c !important;
    margin-top: 25px !important;
    padding-bottom: 25px !important;
  }
  #dashboard_index .new_post {
    border: none !important;
  }
  #dashboard_index .post_title {
    color: #dd0000 !important;
    font-weight: bold !important;
  }

  #dashboard_index .post_title a {
    color: #dd0000 !important;
    font-weight: bold !important;
  }

  #dashboard_index img.permalink {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 15px !important;
    padding-top: 15px !important;
    margin-top: 2px;
    background: url('http://img73.imageshack.us/img73/5696/41251446.png') no-repeat !important;
  }
  #dashboard_index #next_page_link, #previous_page_link {
    background: none #FFFFFF !important;
  }

  #dashboard_index #next_page_link, #previous_page_link a {
    color: #555555 !important;
  }


  #dashboard_index #footer, #dashboard_index #footer a {
    color: #FFFFFF !important;
  }


  #dashboard_index #dashboard_activity {
    background: none #ffffff !important;
  }
  #dashboard_index .dashboard_nav_item a {
    color: #555555 !important;
}
  #dashboard_index .dashboard_nav_item img {
    display: none !important;
  }
}
