css:
@-moz-document domain("paladins.guru") {
body{
    color: #d2d2d2;
    background-color: #2b2b2b !important;
}
section > div.container, .page-content, #cw div[data-v-7b66b580] section{
    background-color: #2b2b2b !important;
}
.counts, .section-break, #cw > section[data-v-7b66b580] > section{
    background-color: #2b2b2b !important;
}
section > div.container.profile-header{
    background-color: #fff0 !important;
}
.add-c{
    display: none;
}
div.column.col-4.col-md-12{
    display: none;
}
.column.col-8.col-md-12{
    width: 100%;
}
div.match-widget__body{
    background-color: #484848;
}
.profile-header__level{
    background-color: #2b2b2b;
}
.match-widget__stats{
    color: #d2d2d2;
}
.match-widget__kills {
    color: #259d58;
}
.match-widget--title, .match-widget__assists {
    color: #a9a9a9;
}
a{
    color: #d2d2d2 !important;
}
h1.section-header--h1{
    color: #d2d2d2;
}
.match-table__body{
    color: #d2d2d2;
}
.loss .match-table__row[data-v-8156ae60]:nth-child(odd) {
    background: #611818;
}
.loss .match-table__row[data-v-8156ae60]:nth-child(2n) {
    background: #891b1b;
}
.win .match-table__row[data-v-8156ae60]:nth-child(odd) {
    background: #145239;
}
.win .match-table__row[data-v-8156ae60]:nth-child(2n) {
    background: #197741;
}
.insight-bar__grid .bar[data-v-7ba3c9b4]:before {
    background-image: linear-gradient(
        90deg,
        #2b2b2b,
        #2b2b2b 4px,
        transparent 0,
        transparent 10px
    );
}
.insight-bar__grid .bar[data-v-7ba3c9b4] {
    transform: skewX(10deg);
}
.row[data-v-235f908c] {
    background: #2368a2;
}
.match-table__row[data-v-eefa58aa]:nth-child(2n) {
    background: #3183c8;
}
.row[data-v-27041924] {
    background: #2368a2;
}
.match-table__row[data-v-0507dc8b]:nth-child(2n) {
    background: #3183c8;
}
.box {
    background: #272727;
}
.status-widget {
    background: #5f5f5f;
}
.menu {
    background: #5f5f5f;
}
/* BUILD SECTION*/
.widget > div.has-icon-right > input{
    display: none;
}
.widget .has-icon-right .form-icon {
    right: 0.8rem;
    top: 1.5rem;
}
.icon-search:before,.icon-search:after{
    display: none;
}
div[style="margin-top: 1em; display: flex; flex-wrap: wrap; justify-content: space-between;"]{
    padding-right: 50px;
}
.widget{
    background: #3183c8;
}
.build__loadout, .item-row[data-v-7ddcf57c]{
    background: #5f5f5f;
}
.build__loadout__level, .build__loadout__name{
    color: #d2d2d2;
}
.mpc__champion:nth-child(2n) {
    background: #389bef;
}
.tsw__title[data-v-1233f40d] {
    background: #1a4971;
}
.tsw__grid__title[data-v-1233f40d] {
    color: #d2d2d2;
}
.mpc__stats>div:nth-child(2), .mpc__portrait__class {
    color: #d2d2d2;
}
.page-content > [data-v-3a78a05e]{
    background: rgb(49 131 200) !important;
}
.row[data-v-13192073] {
    background: #2368a2;
}
.champion-table__row[data-v-1c535d7c]:nth-child(2n) {
    background: #3183c8;
}
.row__champion__name[data-v-13192073],.row__champion__class[data-v-13192073],.percentile-stat__label{
    color: #d2d2d2;
}
.row__champion{
    padding-left: 10px;
}

/* GUIDE SECTION*/
.list[data-v-53015e48]{
    background: #5f5f5f;
}



.guide-side[data-v-a4ff13d0]{
    background: #5f5f5f;
}
p[data-v-a4ff13d0].text-center{
    color: #d2d2d2!important;
}
/* Tierlist SECTION*/
.list[data-v-470ac420]{
    background: #5f5f5f;
}

/*search*/
#cw > section[data-v-7b66b580] > div[style]{
    background-color: #2b2b2b !important;
}
.search-player-widget{
    background-color: #5f5f5f !important;
}

/*----------ADS BLOCKiNG SECTION----------*/
div[style="background: #1c1d1ee6; display: flex;padding: 1em 0;"],div[style="background: rgba(28, 29, 30, 0.9); display: flex; padding: 1em 0px;"]{
    display: none !important;
}
div[style="position: fixed; bottom: 100px; right: 0px; width: 400px; height: 225px; overflow: hidden; z-index: 1000;"]{
    display: none !important;
}
div.zaf-sticky-bottom-center{
    display: none !important;
}
}/*https://userstyles.org/users/408191*/
