css:
@-moz-document domain(www.stockfetcher.com)
{
html, body {
    background-color: #222;
    color: #ddd;
}
.panel {
    background-color: #222;
}
.panel-default > .panel-heading {
    background-color: #282828;
    color: #ddd;
}
.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #222;
}
.table-striped > tbody > tr:nth-of-type(2n) {
    background-color: #282828;
}
.chart-result-box {
    background-color: #222;
}
.main-panel {
    background-color: #222;
    border: 1px solid #666;
}
.panel-footer,
.row {
    background-color: #222;
}
.filter-preview-panel-box {
    background-color: #222;
}
#filter-text-preview {
    color: #ddd;
}
.filter-preview-title {
    background: #282828;
}
.fb-table tbody > tr.row-hover > td,
#filter-text-preview:hover {
    background-color: #303a50 !important;
}
.chart-facts-table td {
    color: #000;
}
.CodeMirror {
    filter: invert(.9);
    /**background-color: hsl(220, 20%, 60%);
    color: #ddd**/
}
.well {
    background-color: #282828;
}
.bottom-nav {
    background: #282828;
}
.forum-message-table.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #282828;
}
.forum-message-table.table-striped > tbody > tr:nth-of-type(2n) {
    background-color: #242424;
}
.featherlight .featherlight-content {
    background: #282828;
}
.chart-fact-header {
    color: #ddd;
    background-color: #2a2a2a !important;
    border: 1px solid #aaa;
}
.chart-facts-table tr > th, .chart-facts-table tr > td {
    background-color: #333;
    color: #ddd;
}
.multichart, .canvas-only {
    filter: invert(.9) hue-rotate(180deg) saturate(4);
}
.mf-category > a,
.label.label-primary.label-pill {
    color: #aaa;
}
.wl-row.active > td, .fb-table tbody > tr.active > td, .fb-table tbody > tr.active.row-hover > td {
    background-color: hsl(221, 45%, 25%) !important;
    border-color: hsl(221, 45%, 40%) !important;
}
.modal-content,
.webui-popover-content {
    background-color: #333;
}
.webui-popover {
    background-color: #555;
}
.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    border-top: 1px solid #666;
}
.table > thead > tr > th,
.filter-preview-title {
    border-bottom: 2px solid #666;
}
.fb-table tr.row-hover > td > i.fa {
    color: #aaa;
}
.fb-table tr.active > td > i.fa, .fb-table tr.active > td > a.edit-link {
    color: #ccc;
}
.fb-table tr.active.row-hover > td > i.fa, .fb-table tr.active.row-hover > td > a.edit-link {
    color: #ddd;
}
.filter-preview-panel-box {
    border: 1px solid #666;
}
.btn-default {
    color: #ddd;
    background-color: #333;
    border-color: #555;
}
.btn-default:hover {
    color: #ddd;
    background-color: #444;
    border-color: #666;
}
.btn-default.disabled, .btn-default.disabled.active, .btn-default.disabled.focus, .btn-default.disabled:active, .btn-default.disabled:focus, .btn-default.disabled:hover, .btn-default[disabled], .btn-default[disabled].active, .btn-default[disabled].focus, .btn-default[disabled]:active, .btn-default[disabled]:focus, .btn-default[disabled]:hover, fieldset[disabled] .btn-default, fieldset[disabled] .btn-default.active, fieldset[disabled] .btn-default.focus, fieldset[disabled] .btn-default:active, fieldset[disabled] .btn-default:focus, fieldset[disabled] .btn-default:hover {
    background-color: #222;
    border-color: #444;
}
.form-control {
    color: #ddd;
    background-color: #333;
    border: 1px solid #666;
}
.navbar-nav > li.active > a {
    background: hsl(221, 45%, 25%) !important;
    color: #fff !important;
    border-bottom: 0px solid #416DD4;
}
.navbar-container {
    border-bottom: 1px solid hsl(221, 45%, 25%);
}
.sf-rating a {
    color: #ccc;
    background-color: #282828;
    text-shadow: none;
}
}
