css:
@-moz-document url-prefix("https://defector.com/") {
main, body, html {
    background: #302929;
    color: #eee;
}
main :first-child {
    z-index: 0;
}
.defector__article-content h1 a, .defector__article-content h2 a, .defector__article-content h3 a, .defector__article-content h4 a, .defector__article-content h5 a, .defector__article-content h6 a, .post-card__eyebrow, .irving__term-link--eyebrow, .irving__site-menu--footer li > a {
    color: #aaf;
}
.irving__link--commentCount, .fYMlyv p, .irving__text, .post-card__comment-count, .iPXMKT, .irving__fragment, .post-card__title, .irving__post-title, .irving__post-excerpt, .related-post__inner .related-post__heading, .ticker__item {
    color: #eee;
}
.gFRwaY {
    background: #331000;
}
.aWmwc {
    background: #443535;
}
.aWmwc span {
    color: #eee;
}
.irving__link--more {
    background: #1D4378;
}
.defector__article-content p, .defector__article-content ul li, .defector__article-content ol li {
    color: #eee;
}
.dweAQA, .jCZWvl > div:first-child {
    border-color: #eee
}
.irving__post-timestamp, figcaption {
    color: #aaa;
}
.related-post__inner {
    background: #1D4378;
}
.dVrWnO, .dVrWnO::before {
    background: none;
}
}

@-moz-document url-prefix("https://defector-coral.herokuapp.com/") {
main, body {
    background: #302929;
    color: #eee;
}
:root {
    color: #eee;
    --palette-text-500: #ccc;
    --palette-primary-500: #eee;
    --palette-background-body: #302929;
    --palette-grey-300: #aaa;
    --palette-primary-100: #333232;
    --palette-text-900: #8a8aaf;
    --palette-primary-500: #8a8aaf;
    --palette-grey-100: #292828;
    --palette-grey-200: #333232;
    --palette-background-input: #333232
}
.coral-comment-username > span {
    color: #eee;
}
.coral-comment-reactButton span {
    color: #eee;
}
.coral.coral-comment-actionBar .coral-comment-reactButton::before {
    color: #333;
}
.coral-comment-inReplyToText {
    color: #aaa;
}
.coral-guidelines-content a {
    color: #8a8aaf;
}
}
