/*一般登入*/
form[name="fm_login"]{
    position: absolute;
    overflow-y: auto;
    width: 40vw;
    height: 40vh;
    left: 5vw;
    top: 12vh;
}

/*訪客登入*/
body>table:first-of-type tr:last-of-type{
    position: absolute;
    overflow-y:auto;
    width: 40vw;
    height: 40vh;
    right: 5vw;
    top: 12vh;
}

/*登入提示*/
center>table{
    width: 100%;
    height: 100%
}
center{
    width: 90vw;
    height: 30vh;
    position:absolute;
    top: 60vh;
    left: 5vh;
    overflow-y: auto;    
}
}
