css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flickr.com") {

body { background: #253960 url("data:image/png;base64,") top left repeat-x !important; }

body * { font-family: "Gill Sans", "Trebuchet MS", Helvetica !important; }

h1,h2,h3,h4,h5,h6, a:hover { color: #f09 !important; }

a:hover { background: none !important; }

/*h4:hover { background-color: #f09 !important; }*/

.explain, .PhotoStream { color: #fff !important; }

.AtStart, .Paginator a, .Paginator .this-page, .break{ background-color: #253960 !important; border: none !important; }

.AtStart { color: #fff !important; }

[name="content"], [name="content"] * { color: #fff !important; background-color: transparent !important; border: 1px solid !important;}

/*[title="Click to edit"]:hover { background-color: #00f !important }*/

.CollectionsColumn { background: none !important; }

#Main, .Footer { border-left: 1px solid #fff !important; border-right: 1px solid #fff !important; }

#Main { border-bottom: 1px solid #fff !important; border-top: 1px solid #fff !important; margin-top: 18px !important; }

#Main p, small, .Stats td, #Left span, .fn, .n, .RealName { color: #fff !important; }

.Footer, .Footer .To, .Footer .To a, .Who b { color: #fff !important; }

.Footer { margin-bottom: -5px !important; border-bottom: 1px solid #fff !important; }

[src="http://l.yimg.com/www.flickr.com/images/spaceball.gif"] { display: none; }

#Feeds #AtomRSS { color: #fff !important; position: relative !important; top: 177px !important; width: 100% !important; text-align: center !important; }

#Feeds #AddToYahoo, [alt="Looking for the perfect gift?  Give the Gift of Flickr!"] { display: none !important; }

.TopBar { background: none !important; width: 760px !important; border-left: 1px solid #fff !important; border-right: 1px solid #fff !important; border-bottom: 1px solid #fff !important; margin-left: auto; margin-right: auto; padding: 0 10px 10px 10px; }

.Header, .NavBar { width: 760px !important;  }

.TopBar { background: #fff !important; }

/*.TopBar * { opacity: 0.7; }*/

.PhotoStream, .SlideShowTop { background: none !important; }

[alt="Flickr, a Yahoo! Company"], .crap { display: none !important; }

.Coll-l { padding-top: 20px !important; float: none !important;  }

.Here, .TabIn { color: #f0A !important; }

ul li { color: #fff !important; list-style-type: none !important;}

.Focus, #TagCloud, .Intro, .Intro *, .Separated, .Separated *, #favoriteThumbs, .HomeBox, .InvitePreview, .BigNumber, .For,  .acc-sum, .acc-sum td, #Main table tr td, .PoolList, .TopicListing th, .break, textarea { background: none !important; color: #fff !important; }

.TopicListing th { color: #f09 !important; }

.YearListing li { list-style-type: none !important; position: relative !important; left: -35px !important;}

.Stats ul { position: relative !important; left: 20px !important; }

#favoriteThumbs p { font-size: 80% !important; }

.Tab, .TabIn, .TabOut, .Areas * { background: none !important; border: none !important; padding-bottom: 5px !important; }

.Tab, .Areas {border-bottom: 1px solid #fff !important; }

.SlideShowTop { border: none !important; }

.Status { color: #000 !important; }

.Desc * { color: #fff !important; }

.Desc:hover * { background-color: #00f !important; }

.CollCase { background: none !important; }

#Main .StreamView *, #Photo *  { background: none !important; }

.CollCase img { border: 3px double #fff !important; }

.Highlight { background-color: #00f !important; }

.contexTop, .contexTop *, .contextTitleOpen, .contextTitleClosed, .contextThumbsRow *, .ContextsOther, .ContextsOther *, #otherContexts_div, .contextCrumbsDiv, .currentContextLink { border: none !important; background: none !important; }

}
