css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#downloadManager{
-moz-appearance: -moz-win-glass !important;
background:transparent !important;
}

#downloadView {
opacity: .99 !important;
margin-left: 2px !important;
margin-right:2px !important;
background: rgba(255,255,255, .99) url("data:image/png;base64,") !important;
background-repeat: no-repeat !important;
background-position: center !important;
}

#downloadView > richlistitem{

background: rgba(255,255,255,0.5) !important;
}

#downloadView > richlistitem[selected=true] {
-moz-appearance:none;
color: black !important;

background: rgba(0,120,255,0.4) !important;
}

#downloadView > richlistitem:hover {
background: rgba(0,120,255,0.2) !important;
}

#downloadView > richlistitem[selected=true]:hover {
background: rgba(0,120,255,0.3) !important;
}

#clearListButton{
margin-top:8px!important;
margin-bottom:3px!important;
width:145px!important;
margin-left: 0px !important;
} 

#clearListButton[disabled="true"]{
display: none !important;
}

#searchbox{
margin-top:8px!important;
margin-bottom: 3px!important;
margin-right: 2px !important;
min-width: 200px!important;

}
