css:
@-moz-document domain("dappervolk.com") {
#top-nav, #hub-nav {
    height: 50px;
}

#top-nav > .container,
#hub-nav > .container,
#top-nav > .container > .nav,
#hub-nav > .container > .nav,
#top-nav > .container > .nav > li > a,
#hub-nav > .container > .nav > li > a {
    height: 100%;
}

#top-nav > .container > .nav,
#hub-nav > .container > .nav {
    display: flex;
    flex-direction: row;
    align-items: stretch;
}

#top-nav > .container > .nav > li > a,
#hub-nav > .container > .nav > li > a{
    display: flex;
    justify-content: center;
    align-items: center;
}

.clock > .icon {
    margin-right: 2px;
}
}
