css:
@-moz-document regexp("https://www\\.dpg-verhandlungen\\.de/year/.*/conference/.*/part/.*/session/.*/contribution/.*") {
body {
    margin: 0 auto;
    max-width: 80em;
    font-size: 120%;
    background-color:#aaa;
    font-family: Helvetica, Roboto, 'Noto Sans', sans-serif;
}
h3 {
    display:inline-block;
    margin: 0;
    margin-right: 2em;
}
h1, h2, h3 {
    font-size: 80%;
    margin-top: 0;
    margin-bottom: 0;
}
div.content {
	padding: 1em 3em;
    background-color: #eee;
    column-count: 2;
    column-gap: 2em;
}
.content p {
    line-height: 180%;
    margin-top: 0;
}
.content form{
    line-height:110%;
}
div.head2 {
    display:none;
}
}
