@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(free-lance.ru) {



/* -- Global -- */
::-moz-selection { color: #fff !important; background: #059bcd !important; }

input[alt="Ð’Ñ…Ð¾Ð´"] { width: 48px !important; }



/* -- Header -- */
div.header div.header-advert, dl.last-news, table[border="0"] tr[valign="top"] td[style="padding: 10px 10px 0pt;"] { vertical-align: middle !important; }
	div.header div.header-advert {
		display: table !important;
		width: 100% !important;
		margin: 0 0 12px !important;
	}
		dl.last-news { float: none !important; display: table-cell !important; }

.h-menu { padding: 7px 0 !important; }
td.frlblg table td.frlblg  { padding: 5px 0 !important; }

#acc_switch td.intd { height: 32px !important; }

.mb-account a.lnk-acc-change span, #acc_switch td.intd span.dot { border: 0 !important; }



/* -- Content -- */
.pc-left .pc-menu { -moz-border-radius: 7px; border-radius: 7px; }

.main-free-answer, div.pf-canvas, #right_col .item .content_with_image .contacts { -moz-border-radius: 5px; border-radius: 5px; }

.pc-left .pc-menu { border: 1px solid #eee !important; }

.main-free-answer { border: 1px solid #ffeae3 !important; }

div.pf-canvas { border: 1px solid #eeedeb !important; }

#right_col .item .content_with_image .contacts { border: 1px solid #f4f4f4 !important; }

div[style="padding: 10px 0pt 10px 10px; background: rgb(232, 232, 232) none repeat scroll 0% 0%; width: 200px; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial;"] { border: 1px solid #e8e8e8 !important; -moz-border-radius: 5px; border-radius: 5px; }

div[style="padding: 16px 12px; background-color: rgb(243, 243, 243); margin-top: 31px;"] {
	padding: 15px 11px !important;
	border: 1px solid #f3f3f3 !important;
	-moz-border-radius: 7px;
	border-radius: 7px;
}

#accordion li.all-employers a, #accordion li.all-lancers a, #fl2_sidemenu div.all_freelancers a {
	padding: 2px 0 3px 23px !important;
	border: 2px solid #6bb24b !important;
	-moz-border-radius: 5px;
	border-radius: 5px;
}

#accordion a.toggler, #fl2_sidemenu div.display a {
	padding: 2px 0 3px 23px !important;
	border: 2px solid #89D363 !important;
	-moz-border-radius: 5px;
	border-radius: 5px;
}
	#fl2_sidemenu div.display, #fl2_sidemenu div.all_freelancers { background: none !important; }
		#fl2_sidemenu div.display a, #fl2_sidemenu div.all_freelancers a { display: block !important; }

#accordion li li { padding-bottom: 3px !important; }
	#accordion li li .this { font-weight: 700 !important; }

img[src="/images/post_button.gif"] { width: 178px !important; }

table.pgs[border="0"] td { padding: 0 !important; border: 0 !important; }

#tags .pagination { text-align: center !important; }

div.pager a,
div.pager span,
#fl2_paginator td[align="center"] a,
#fl2_paginator td[align="center"] b,
table.pgs[border="0"] td.box,
table.pgs[border="0"] td a,
#tags .pagination a,
#tags .pagination span {
	display: inline-block !important;
	text-decoration: none !important;
	margin: 0 2px !important;
	padding: 3px 5px !important;
	border: 1px solid #ddd !important;
	-moz-border-radius: 3px;
	border-radius: 3px;
}
	#tags .pagination a { margin: 3px 1px !important; }
	div.pager a, div.pager span { margin: 0 !important; }
	div.pager a:hover,
	#fl2_paginator td[align="center"] a:hover,
	table.pgs[border="0"] td a:hover,
	#tags .pagination a:hover {
		color: #444 !important;
		border-color: #bbb !important;
	}
	div.pager span.page,
	#fl2_paginator td[align="center"] b,
	table.pgs[border="0"] td.box,
	#tags .pagination span {
		font-weight: 700 !important;
		color: #888 !important;
		background: #eee !important;
	}
	div.pager span.page-next, div.pager span.page-back { padding: 2px 5px !important; }
		div.pager span.page-next a,
		div.pager span.page-back a {
			display: inline !important;
			padding: 0 !important;
			border: 0 !important;
		}
#fl2_paginator td[align="center"]  span { padding: 0 5px !important; }

div.box #fl2_search_input2[name="search_string"], td[style="width: 257px;"] input.search-input { width: 173px !important; }

div.box ul.group li, td[style="width:257px"] ul.groups li { border-bottom-color: #ddd !important; }

a[href^="http://top100.rambler.ru/home?id=1367737"],
td#acc_switch a[href^="/payed"],
#mb-account a[href^="/payed"],
div.banner_240x400,
td[style="width: 240px;"],
table[bgcolor="#FFFFFF"] td[height="247"] div.errorBox2 {
	display: none !important;
}

form#lentaForm { width: 240px; }
}
