css:
@-moz-document domain(teamwork.com) {
/* DON'T CUTOFF TASK NAMES */
.w-task-row__name a, .w-task-row__name span {
  overflow: initial !important;
  text-overflow: initial !important;
  white-space: initial !important;
}

/* MOVE TAKS STATUS SECOND */
.task-row .row-content-holder .w-task-row__bubble {
    order: 1;
}
.task-row .row-content-holder .boardColumn-label {
    order: 2;
}
.task-row .row-content-holder > * {
    order: 3;
}

/* NOTIFICATIONS POPOVER */
.w-notifications {
    width: 800px;
}

.w-notifications__item {
    width: 780px;
}
}
