css:
@-moz-document url-prefix("https://f1tv.formula1.com/detail/") {
	.sticky-header-wrapper {
        margin-bottom: 48px !important;
    }

    .sticky-header-wrapper .global-header {
        height: 0px;
    }

    .sticky-header-wrapper .global-header-links {
        display: none;
    }

    .sticky-header-wrapper .global-header-actions {
        position: absolute;
        top: 0px;
        right: 48px;
        z-index: 10000;
    }
    .sticky-header-wrapper .global-header-actions .user-name {
        color: #fff;
    }

    .sticky-header-wrapper .header .navbar {
        height: 48px;
        background-color: #000;
    }
    .sticky-header-wrapper .header {
        background-color: #000;
    }

    .vod-detail-page .container-lg:first-child {
        max-width: 100%;
    }

    .vod-detail-page .col-xl-10.offset-xl-1 {
        max-width: 100vw;
        flex: none;
        margin: 0;
        padding: 0;
    }

    .vod-detail-page .col-xl-10.offset-xl-1 .mt-4 {
        margin-top: 0 !important;
    }

    .vod-detail-page .inset-video-item-image-container.d-flex {
        display: block !important;
    }

    .vod-detail-page .inset-video-item-image-container {
        max-height: calc(94vh - 48px);
    }
}
