css:
@-moz-document domain("duckduckgo.com") {
body {
    background: #fcfcfc!important;
}

strong,
b {
    font-weight: 700!important;
}

.result__a,
.results .highlight,
.result {
    color: #0062D1;
    font-weight: 500
}


.dark-bg .result__a {
    color: #6FB5FF!important;
}

.result__a:visited {
    color: #631A9D;
}

.dark-bg .result__a:visited {
    color: #AF88CD!important;
}

.cw {
    margin-left: auto;
}

.search--header {
    width: 700px
}

.zcm-wrap--header {
    min-width: 700px
}

.zcm-wrap-wrap {
    padding-top: 10px;
}

.results--main {
    max-width: 720px;
}

.result--url-above-snippet .result__snippet {
    line-height: 22px;
    margin-top: 5px;
    padding-top: 5px;
    border-top: 1px solid rgba(0, 0, 0, .1);
}

.dark-bg .result--url-above-snippet .result__snippet {
    border-top: 1px solid rgba(255, 255, 255, .1);
}

.results {
    margin-top: 0;
    margin-right: 1em;
}

.result__body {
    background: #fff;
    -moz-border-radius: 5px;
    border-radius: 3px;
    -webkit-border-radius: 3px;
    border: 1px rgba(0, 0, 0, .1) solid;
    padding: 10px 15px;
}

.results .highlight,
.result {
    border: none!important;
    background: none!important;
    box-shadow: none!important;
}

.dark-bg .result__body {
    background: rgba(255, 255, 255, .05);
    border: 1px rgba(255, 255, 255, .1) solid;
}


.result__url__domain {
    color: #222;
}

.dark-bg .result__url__domain {
    opacity: .7;
    color: #fcfcfc;
}

.result__url__full {
    opacity: .5!important;
    color: #222;
}

.dark-bg .result__url__full {
    opacity: .3!important;
    color: #fcfcfc;
}

.header__search-wrap {
    padding-right: 0;
}
}
