css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.cityofheroes.com")

{

/* Change font and color for title and date on thread lists */
  #threadslist .alt1 *, #threadslist .alt2 *, #threadslist .alt2 {
    font-family: Verdana, sans-serif !important;
    color: #ffffff !important;
  }


/* Change font for posts and forum headings */
  #posts *, #collapseobj_threadreview * {
    font-family: Verdana, sans-serif !important;
  }
  .alt1, .alt2, .alt1 *, .alt2 *, .alt1Active *, .alt2Active * {
    font-family: Verdana, sans-serif !important;
  }

/* name fix */
   .bigusername, .bigusername * {
       font-weight: bold !important; 
    }

/* Change color for titles and authors in a post */
  #posts .smallfont > strong,
  #posts a.bigusername,
  #posts tr > td.alt2 > div:first-child,
  #posts tr > td.alt2 > div:first-child > * {
    color: #95e65b !important;
    font-weight: bold;
  }

/* Disable underlines on thread list */
  #threadslist .alt1 a, #threadslist .alt2 a {
    text-decoration: none !important;
  }


/* Disable underlines on Post user names */
  a.bigusername {
    text-decoration: none !important;
  }

 
/* Render authors in a darker color on thread list */
  #threadslist .alt1 .smallfont > span[style],
  #threadslist .alt2 .smallfont > br + span,
  #threadslist .alt2 .smallfont > span > a {
    color: #b4b5de !important;
  }
  table.tborder tr td.alt1 strong + span.smallfont:before {
    content: "> " ;
  }


/* Eliminate background gradients and set the bgcolor of the two main panels */
  td.alt1, td.alt2, td.alt1Active, td.alt2Active {
    background-image: none !important;
  }
  td.alt1, td.alt1Active {
    background: #244a79  !important;
  }
  td.alt2, td.alt2Active {
    background: #012c61 !important;
  }

/* Change link colors */
  a:link, body_alink {
    color: #95e65b !important;
    font: 10pt Verdana bold !important;
    text-decoration: underline;
  }
  a:visited, body_avisited {
    color: #c1ffe6 !important;
    text-decoration: underline;
  }


/* Changes the wysiwyg editor background to mimic the board's color set above */
    .wysiwyg {
    background: #244a79 !important;
    color: #000000;
    font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
    margin: 5px 10px 10px 10px;
    padding: 0px;
    }
    .panel {
    background: #012c61 !important;
    color: #ffffff;
    padding: 10px;
    border: 1px;
    }


/* Eliminate background to the avatars so that .gifs with alpha channels work */
    .avatar {
    background: none !important;
    height:110px !important;
    width:110px !important;
    }
    .avatar_pic {
    border: none !important;
    padding-top:4px;
    }


/* Eliminate background pic of the forum (sig heros) */
    body {
    background: #03060B !important;
    color: #ffffff !important;
    font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
    margin: 0px 10px 10px 10px;
    padding: 0px;
    }

    .page_bgl {
      background-image: none !important;
    }

    .page_bgr {
      background-image: none !important;
    }

    .tborder {
      background: black !important;
    }

    .tborder td {
      border-color: black !important;
    }


/* Increases default font size from 10 to 11 */
    td, th, p, li {
    font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
    }   


/* Changes the new-posts arrow in the "Thread / Thread Starter" column */
  img[src*="styles/CoH/buttons/firstnew.gif"] {
    width: 0;
    height: 0;
    background-repeat: no-repeat;
    background-image: url(http://i32.tinypic.com/33ur9eo.png) !important;
    padding: 20px 20px 0 0 !important;
}


/* Changes the "go to last post" arrow by the name in the "Last Post" column */
    img[src*="styles/CoH/buttons/lastpost.png"] {
    width: 0;
    height: 0;
    background-repeat: no-repeat;
    background-image: url(http://i29.tinypic.com/xva8k.png) !important;
    padding: 20px 20px 0 0 !important;
    }

}
