css:
@-moz-document domain(wildberries.ru) {
.header-v1 {
    background: linear-gradient(to right,#2f2f2f 0,#2f2f2f 100%) no-repeat;
}

.main-content {
    background-color: #2f2f2f;
}

.products-container .item-info .good:after {
    background: -webkit-gradient(linear,right top,left top,from(#fff),to(rgba(255,255,255,0)));
    background: linear-gradient(to left,#2f2f2f,rgba(255,255,255,0));
}

.main-content:before {
    background-color: #2f2f2f;
}

.i-additional-text .additional-wrapper:after {
    background: -webkit-gradient(linear,left top,left bottom,from(rgba(255,255,255,0)),to(#fff));
    background: linear-gradient(rgba(255,255,255,0),#2f2f2f);
}

.footer:before {
    background: #2f2f2f;
}

.footer {
    background: #303030;
    margin-top: 0;
}

.i-lk-menu-top .level-main {
    background: #6b6b6b;
}

.i-lk-menu-top .lk-menu-top-wrap .lk-submenu {
    background: #2f2f2f;
}

.header-with-burger .i-tinyscrollbar-topmenus {
    background-color: #333;
}

.header-with-burger .dropdown-item li.menu-banner .menu-banner-text:after {
    background: -webkit-gradient(linear,right top,left top,from(#fff),to(rgba(255,255,255,0)));
    background: linear-gradient(to left,#333,rgb(51 51 51));
}

.i-tooltip-img-upload {
    background: #505050;
}

.trunkOld {
    background-color: #2f2f2f;
}

.c-important-msg {
    background: #000000;
}

.faq-menu-title {
    /* font: normal 13px/1.2 Tahoma; */
    /* color: #2d2d2d; */
    background: #404040;
}

.header-v1 .lk-menu-wrapper {
    background: #383838;
}

/* ТЕКСТ */

.c-h1 {
    color: #fff;
}

.i-filters .c-checkbox-withText-v1, .i-filters .c-radio-withText-md {
    color: #cecece;
}

.i-same-part-kt .first-horizontal .brand-and-name {
    color: #fff;
}

.i-same-part-kt .card-row .card-right .order-block .inner-price .final-cost {
    color: #fff;
}

.i-same-part-kt .card-row .card-right .color span {
    color: #d2d2d2;
}

.c-text-base-v1 {
    color: #8b8b8b;
}

.brands-group .column li a {
    color: #8b8b8b;
}

.sell-on-wb__features .sell-on-wb__text {
    color: #8e8e8e;
}

.delivery-map-wrap-left .address-list-item-name {
    color: #8b8b8b;
}

h3 {
    color: #cecece;
}

/* ТЕКСТ */

.i-dtList .colors-preview {
    background: #505050;
}

.i-dtList .dtlist-dop-inner {
    background-color: #505050;
}

.i-dtList .dtlist-dop {
    background: #505050;
}

.trunkOld .lk .lk-content #ponedList .postponed>.item .item-block .info .details .title:after {
    background: -webkit-gradient(linear,right top,left top,from(#fff),to(rgba(255,255,255,0)));
    background: linear-gradient(to left,#2f2f2f,rgba(255,255,255,0));
}

.trunkOld .lk .lk-content #ponedList .postponed>.item .item-block .info .details-line .price:after {
    background: -webkit-gradient(linear,right top,left top,from(#fff),to(rgba(255,255,255,0)));
    background: linear-gradient(to left,#2f2f2f,rgba(255,255,255,0));
}

.i-selected-products {
    background: #2f2f2f;
}


.comment-content, .comment-profile, .question-content, .question-profile {
    color: #cecece;
}

.delivery-banner {
    background: linear-gradient(89.51deg, #77391c 2.39%, #581b43 99.79%);
}

.delivery-widget {
    background: #721468;
}
}
