@-moz-document regexp(".*://www.facebook.com.*") {
/*--[Universal]--*/

/*Home sidebar*/
.fer614ym{
  transition: flex-basis .25s;
  max-width: unset;
  min-width: unset;
}

.fer614ym:not(:hover){
  flex-basis: 60px;
}

.fer614ym span{
  white-space: nowrap;
  transition: opacity .25s .25s;
  opacity: 1;
}

.fer614ym:not(:hover) span{
  opacity: 0.00;
}

/*HOME right sidebar sponsored (hidden)*/
[data-pagelet="RightRail"]>div:first-child{
    height: 0;
    overflow: hidden;
}


/*HOME post creating section*/
.o0xt3n8b{
    max-width:unset;
}


/*main contain*/
.oh7imozk{
  max-width: 98%;
  width: 750px;
}

