css:
@-moz-document domain("finobe.com") {
.navbar-brand {
    background-position: center;
    padding-top: .5rem;
    font-size: 1rem;
    background-image: url(https://media.discordapp.net/attachments/409970143939264512/790509522707808276/Finobe_logo9_2020.png);
    background-size: contain;
    width: 100px;
    background-repeat: no-repeat;
    color: transparent!important;
    margin-right: 0px;
}

.navbar-brandimg {
    height: 0px;
}

.bg-dark {
    background-color: #343a40!important;
}

.navbar {
    position: relative;
    padding: .2rem 1rem;
}

.nav-scroller {
    position: relative;
    z-index: 2;
    height: 1.9rem;
    overflow-y: hidden;
    box-shadow: 0 0.25rem 0.75rem rgb(0 0 0 / 6%);
}

.nav-scroller .nav {
    display: flex;
    flex-wrap: nowrap;
    padding-bottom: 1rem;
    margin-top: -7px;
    width: 100%;
    overflow-x: auto;
    text-align: center;
    white-space: nowrap;
    -webkit-overflow-scrolling: touch;
}

.bg-dark {
    background-color: #2b2b2b!important;
}

.nav-scroller.navbar-dark.bg-dark {
    background: linear-gradient(
90deg
,#1c1c1c,#1c1c1c);
}

.navbar-dark.bg-dark {
    background: #6a6aa7;
    /* background: linear-gradient( 
90deg
 ,#265a7f,#545489); */
    border-bottom: 0;
}

.badge-danger {
    color: hsla(0,0%,100%,.7);
    background-color: #2b2b2b;
    border: 1px solid #bb302b;
}

img, svg {
    vertical-align: middle;
    margin-bottom: 2.4px;
}

.dropdown-toggle:after {
    display: inline-block;
    margin-left: .255em;
    vertical-align: .100em;
    content: "";
    border-top: .3em solid;
    border-right: .3em solid transparent;
    border-bottom: 0;
    border-left: .3em solid transparent;
}

.mr-1 {
    margin-right: .25rem!important;
    margin-bottom: 2.05px;
}
}
